    font-size: 0.9rem;
    font-weight: 600;
    letter-spacing: 0.1em;
}

.blog-title {
    color: var(--color-warm-white);
    font-size: 1.4rem;
    font-weight: 700;
    margin: 1rem 0;
    line-height: 1.4;
}

.blog-excerpt {
    color: rgba(245, 245, 220, 0.8);
    line-height: 1.8;
    margin-bottom: 1rem;
}

.blog-link {
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 600;
    transition: var(--transition-smooth);
}

.blog-link:hover {
