    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 3rem;
    margin-top: 4rem;
}

.album-card {
    background: var(--color-dark);
    border-radius: 10px;
    overflow: hidden;
    transition: var(--transition-smooth);
}

.album-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 50px rgba(212, 175, 55, 0.3);
}

.album-cover {
    position: relative;
    aspect-ratio: 1;
    overflow: hidden;
}

.album-cover img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.album-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(10, 10, 10, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: var(--transition-smooth);
}

.album-card:hover .album-overlay {
    opacity: 1;
}

.play-button-large {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    border: none;
    color: var(--color-dark);
    font-size: 2rem;
    cursor: pointer;
    transition: var(--transition-smooth);
    display: flex;
    text-decoration: none;
    text-align: center;
    justify-content: center;
    align-items: center;
}

.play-button-large:hover {
    transform: scale(1.1);
}

.album-info {
    padding: 1.5rem;
}

.album-title {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.5rem;
}

.album-meta {
    color: var(--color-gold);
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
}

.album-details {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.9rem;
}

.player-section {
    padding: 6rem 0;
    background: var(--color-dark);
}

.music-player {
    background: var(--color-anthracite);
    border-radius: 15px;
    overflow: hidden;
    margin-top: 4rem;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
}

.player-header {
    padding: 2rem;
    background: linear-gradient(
        135deg,
        rgba(212, 175, 55, 0.1),
        rgba(244, 208, 63, 0.05)
    );
    border-bottom: 1px solid rgba(212, 175, 55, 0.2);
}

.now-playing-info {
    display: flex;
    align-items: center;
    gap: 1.5rem;
}

.now-playing-cover {
    width: 80px;
    height: 80px;
    border-radius: 8px;
    overflow: hidden;
}

.now-playing-cover img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.track-name {
    font-size: 1.3rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.3rem;
}

.artist-name {
    color: var(--color-gold);
    font-size: 1rem;
}

.playlist {
    max-height: 500px;
    overflow-y: auto;
}

.track-item {
    display: grid;
    grid-template-columns: 40px 40px 1fr auto;
    gap: 1rem;
    align-items: center;
    padding: 1rem 2rem;
    border-bottom: 1px solid rgba(212, 175, 55, 0.1);
