    transition: var(--transition-smooth);
}

.track-item:hover {
    background: rgba(212, 175, 55, 0.1);
}

.track-number {
    color: rgba(245, 245, 220, 0.5);
    font-size: 0.9rem;
    text-align: center;
}

.track-play-btn {
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background: var(--color-gold);
    border: none;
    color: var(--color-dark);
    font-size: 0.8rem;
    cursor: pointer;
    opacity: 0;
    transition: var(--transition-smooth);
}

.track-item:hover .track-play-btn {
    opacity: 1;
}

.track-info-wrapper {
    display: flex;
    flex-direction: column;
}

.track-title-name {
    color: var(--color-warm-white);
    font-weight: 600;
    margin-bottom: 0.3rem;
}

.track-album-name {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.9rem;
}

.track-duration {
    color: rgba(245, 245, 220, 0.5);
    font-size: 0.9rem;
}

.streaming-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.streaming-platforms {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    gap: 2rem;
    margin-top: 4rem;
}

.platform-link {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
    padding: 2rem;
    background: var(--color-dark);
    border: 2px solid rgba(212, 175, 55, 0.2);
    border-radius: 15px;
    color: var(--color-gold);
    text-decoration: none;
    transition: var(--transition-smooth);
    min-width: 150px;
}

.platform-link:hover {
    border-color: var(--color-gold);
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(212, 175, 55, 0.3);
}

.platform-link span {
    font-weight: 600;
}

.contact-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.contact-grid {
    display: grid;
    grid-template-columns: 1fr 1.2fr;
    gap: 4rem;
    margin-top: 3rem;
}

.contact-info {
    background: var(--color-dark);
    padding: 3rem;
    border-radius: 15px;
}

.contact-intro {
    font-size: 1.1rem;
    line-height: 1.8;
    margin-bottom: 2rem;
    color: rgba(245, 245, 220, 0.9);
}

.contact-methods {
    margin-bottom: 3rem;
}

.contact-method {
    display: flex;
    align-items: start;
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.method-icon {
    font-size: 2rem;
    min-width: 50px;
}

.method-info h3 {
    color: var(--color-gold);
    font-size: 1.2rem;
    margin-bottom: 0.5rem;
}

.method-info a {
    color: var(--color-warm-white);
    text-decoration: none;
    transition: var(--transition-smooth);
}

.method-info a:hover {
    color: var(--color-gold);
}

.contact-social h3 {
    color: var(--color-gold);
    margin-bottom: 1rem;
}

.contact-form-container {
    background: var(--color-dark);
    padding: 3rem;
    border-radius: 15px;
}

.contact-form {
    margin-top: 2rem;
}

.form-group {
    margin-bottom: 1.5rem;
}

.form-group label {
    display: block;
    color: var(--color-gold);
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 1rem;
    background: var(--color-anthracite);
    border: 1px solid rgba(212, 175, 55, 0.2);
    border-radius: 8px;
    color: var(--color-warm-white);
    font-family: 'Montserrat', sans-serif;
    font-size: 1rem;
    transition: var(--transition-smooth);
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--color-gold);
    box-shadow: 0 0 0 3px rgba(212, 175, 55, 0.1);
}

.submit-button {
    width: 100%;
    padding: 1.2rem 3rem;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    border: none;
    border-radius: 50px;
    font-weight: 700;
    font-size: 1.1rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.submit-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 30px rgba(212, 175, 55, 0.6);
}

.alert {
    padding: 1rem;
    border-radius: 8px;
    margin-bottom: 2rem;
}

.alert-success {
    background: rgba(76, 175, 80, 0.2);
    border: 1px solid rgba(76, 175, 80, 0.5);
    color: #4caf50;
}

.alert-error {
    background: rgba(244, 67, 54, 0.2);
    border: 1px solid rgba(244, 67, 54, 0.5);
    color: #f44336;
}

.footer-section {
    flex: 1;
    min-width: 250px;
}

.footer-links {
    list-style: none;
    padding: 0;
}

.footer-links li {
    margin-bottom: 0.8rem;
}

.footer-links a {
    color: var(--color-warm-white);
    text-decoration: none;
    transition: var(--transition-smooth);
}

.footer-links a:hover {
    color: var(--color-gold);
}

@media (max-width: 768px) {
    .container {
        padding: 0 1.5rem;
    }

    .mobile-menu-toggle {
        display: flex;
    }

    .mobile-menu-toggle.active span:nth-child(1) {
        transform: rotate(45deg) translate(5px, 5px);
    }

    .mobile-menu-toggle.active span:nth-child(2) {
        opacity: 0;
    }

    .mobile-menu-toggle.active span:nth-child(3) {
        transform: rotate(-45deg) translate(7px, -6px);
    }

    .nav-menu {
        position: fixed;
        top: 80px;
        left: -100%;
        width: 100%;
        height: calc(100vh - 80px);
        background: rgba(10, 10, 10, 0.98);
        flex-direction: column;
        padding: 2rem;
        transition: var(--transition-smooth);
        z-index: 999;
    }

    .nav-menu.active {
        left: 0;
    }

    .nav-link {
        font-size: 1.1rem;
        padding: 1rem 0;
    }

    .hero-title {
        font-size: 2rem;
    }

    .hero-subtitle {
        font-size: 0.85rem;
        letter-spacing: 0.15em;
    }

    .section-title {
        font-size: 1.8rem;
    }

    .page-title {
        font-size: 2rem;
    }

    .cta-button {
        padding: 0.7rem 1.5rem;
        font-size: 0.85rem;
    }

    .cta-button-secondary {
        padding: 0.6rem 1.2rem;
        font-size: 0.8rem;
    }

    .bio-text {
        font-size: 0.95rem;
    }

    .highlight-text {
        font-size: 1.1rem;
    }

    .bio-grid {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .about-bio-grid {
        grid-template-columns: 1fr;
    }

    .about-bio-image {
        position: relative;
        top: 0;
    }

    .qualities-grid {
        grid-template-columns: 1fr;
    }

    .blog-grid {
        grid-template-columns: 1fr;
    }

    .albums-grid {
        grid-template-columns: 1fr;
    }

    .contact-grid {
        grid-template-columns: 1fr;
    }

    .streaming-platforms {
        flex-direction: column;
        align-items: center;
    }

    .track-item {
        grid-template-columns: 40px 1fr auto;
        padding: 1rem;
    }

    .track-play {
        display: none;
    }

    .clips-grid {
        grid-template-columns: 1fr;
    }

    .clip-title {
        font-size: 1.1rem;
    }

    .event-card {
        flex-direction: column;
        text-align: center;
        padding: 1.5rem;
    }

    .event-venue {
        font-size: 1.1rem;
    }

    .event-time {
        font-size: 0.9rem;
    }

    .event-button {
        padding: 0.6rem 1.2rem;
        font-size: 0.8rem;
    }

    .date-day {
        font-size: 1.8rem;
    }

    .date-month {
        font-size: 0.8rem;
    }

    .gallery-grid {
        grid-template-columns: 1fr;
    }

    .footer-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .social-links {
        justify-content: center;
    }

    .footer h3 {
        font-size: 1.3rem;
    }

    .lightbox-prev,
    .lightbox-next {
        font-size: 2rem;
        padding: 0.5rem;
    }

    .lightbox-prev {
        left: 1rem;
    }

    .lightbox-next {
        right: 1rem;
    }

    .bio-section,
    .clips-section,
    .events-section,
    .gallery-section {
        padding: 4rem 0;
    }

    .submit-button {
        font-size: 1rem;
        padding: 1rem 2rem;
    }

    .testimonials-section {
        padding: 4rem 0;
    }

    .testimonials-slider {
        padding: 0 2.5rem;
    }

    .testimonial-card {
        padding: 2rem 1.5rem;
    }

    .testimonial-quote {
        font-size: 1rem;
    }

    .testimonial-author {
        font-size: 1rem;
    }

    .testimonial-role {
        font-size: 0.85rem;
    }

    .slider-nav {
        width: 40px;
        height: 40px;
        font-size: 1.2rem;
    }

    .testimonial-stars {
        font-size: 1rem;
    }
}

/* Latest Release Section Styles */
.latest-release-section {
    padding: 8rem 0;
    background: var(--color-anthracite);
}

.section-subtitle {
    text-align: center;
    font-size: 1.1rem;
    color: rgba(245, 245, 220, 0.8);
    margin-top: -1rem;
    margin-bottom: 3rem;
    letter-spacing: 0.05em;
}

.section-subtitle.centered {
    text-align: center;
}

.latest-clips-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2.5rem;
    margin-top: 4rem;
}

.latest-clip-card {
    background: rgba(26, 26, 26, 0.6);
    border: 1px solid rgba(212, 175, 55, 0.1);
    border-radius: 15px;
    overflow: hidden;
    transition: var(--transition-smooth);
    cursor: pointer;
}

.latest-clip-card:hover {
    transform: translateY(-10px);
    border-color: var(--color-gold);
    box-shadow: 0 15px 40px rgba(212, 175, 55, 0.3);
}

.latest-clip-thumbnail {
    position: relative;
    width: 100%;
    height: 240px;
    overflow: hidden;
}

.latest-clip-thumbnail img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: var(--transition-smooth);
}

.latest-clip-card:hover .latest-clip-thumbnail img {
    transform: scale(1.1);
}

.clip-badge {
    position: absolute;
    top: 1rem;
    right: 1rem;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    padding: 0.4rem 1rem;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    z-index: 2;
}

.latest-clip-info {
    padding: 1.5rem;
    text-align: center;
}

.latest-clip-title {
    font-size: 1.4rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.5rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.latest-clip-views {
    color: var(--color-gold);
    font-size: 0.95rem;
    margin-bottom: 1rem;
    font-weight: 600;
}

.watch-button {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.7rem 1.8rem;
    background: transparent;
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 0.9rem;
    border-radius: 50px;
    transition: var(--transition-smooth);
    letter-spacing: 0.05em;
}

.watch-button:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    transform: scale(1.05);
}

.watch-button i {
    font-size: 1.3rem;
}

/* Carousel Gallery Section Styles */
.carousel-gallery-section {
    padding: 8rem 0;
    background: var(--color-dark);
    overflow: hidden;
}

.carousel-container {
    margin-top: 4rem;
    overflow: hidden;
    position: relative;
    width: 100%;
}

.carousel-container::before,
.carousel-container::after {
    content: '';
    position: absolute;
    top: 0;
    width: 150px;
    height: 100%;
    z-index: 2;
    pointer-events: none;
}

.carousel-container::before {
    left: 0;
    background: linear-gradient(to right, var(--color-dark) 0%, transparent 100%);
}

.carousel-container::after {
    right: 0;
    background: linear-gradient(to left, var(--color-dark) 0%, transparent 100%);
}

.carousel-track {
    display: flex;
    width: fit-content;
    animation: scroll-carousel 120s linear infinite;
}

.carousel-track:hover {
    animation-play-state: paused;
}

.carousel-slide {
    flex-shrink: 0;
    width: 350px;
    height: 250px;
    margin: 0 1rem;
    border-radius: 10px;
    overflow: hidden;
    position: relative;
    transition: var(--transition-smooth);
}

.carousel-slide:hover {
    transform: scale(1.05);
    box-shadow: 0 10px 30px rgba(212, 175, 55, 0.4);
    z-index: 3;
}

.carousel-slide img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
}

@keyframes scroll-carousel {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(-50%);
    }
}

/* Mobile responsive for carousel */
@media (max-width: 768px) {
    .carousel-slide {
        width: 280px;
        height: 200px;
    }

    .carousel-container::before,
    .carousel-container::after {
        width: 80px;
    }
}

/* Enhanced Testimonials Section Styles */
.testimonials-section .testimonial-card {
    min-height: 350px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    transition: var(--transition-smooth);
}

.testimonials-section .testimonial-card:hover {
    transform: scale(1.02);
    box-shadow: 0 20px 50px rgba(212, 175, 55, 0.3);
    border-color: var(--color-gold);
    background: rgba(26, 26, 26, 0.9);
}

.testimonials-section .testimonial-stars {
    text-align: center;
    display: block;
    width: 100%;
}

.testimonials-section .testimonial-quote {
    text-align: center;
    width: 100%;
}

.testimonials-section .testimonial-author {
    text-align: center;
    width: 100%;
}

.testimonials-section .testimonial-role {
    text-align: center;
    width: 100%;
}

/* Trophies Section Styles */
.trophies-section {
    padding: 6rem 0;
    background: var(--color-dark);
}

.trophies-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    gap: 3rem;
    margin-top: 4rem;
}

.trophy-main-card {
    background: rgba(26, 26, 26, 0.8);
    border: 1px solid rgba(212, 175, 55, 0.2);
    border-radius: 15px;
    overflow: hidden;
    transition: var(--transition-smooth);
}

.trophy-main-card:hover {
    transform: translateY(-10px);
    border-color: var(--color-gold);
    box-shadow: 0 15px 50px rgba(212, 175, 55, 0.4);
}

.trophy-main-image {
    position: relative;
    width: 100%;
    height: 300px;
    overflow: hidden;
}

.trophy-main-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: var(--transition-smooth);
}

.trophy-main-card:hover .trophy-main-image img {
    transform: scale(1.1);
}

.trophy-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to top, rgba(10, 10, 10, 0.9) 0%, transparent 50%);
    display: flex;
    align-items: flex-end;
    justify-content: center;
    padding-bottom: 2rem;
}

.trophy-overlay i {
    font-size: 4rem;
    color: var(--color-gold);
    opacity: 0.7;
    animation: pulse 2s infinite;
}

@keyframes pulse {
    0%, 100% {
        opacity: 0.7;
        transform: scale(1);
    }
    50% {
        opacity: 1;
        transform: scale(1.1);
    }
}

.trophy-main-content {
    padding: 2rem;
}

.trophy-main-title {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-gold);
    margin-bottom: 1rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.trophy-main-description {
    color: rgba(245, 245, 220, 0.9);
    line-height: 1.8;
    margin-bottom: 1.5rem;
    font-size: 1rem;
}

.trophy-details-btn {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.8rem 2rem;
    background: transparent;
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    font-weight: 700;
    text-transform: uppercase;
    font-size: 0.9rem;
    border-radius: 50px;
    cursor: pointer;
    transition: var(--transition-smooth);
    letter-spacing: 0.05em;
}

.trophy-details-btn:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    transform: scale(1.05);
}

.trophy-details-btn i {
    font-size: 1.2rem;
}

/* Trophy Modal Styles */
.trophy-modal {
    display: none;
    position: fixed;
    z-index: 2000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.95);
    overflow-y: auto;
    animation: fadeIn 0.3s ease;
}

