    border-radius: 15px;
    max-width: 900px;
    width: 100%;
    max-height: 90vh;
    overflow-y: auto;
    position: relative;
    animation: slideIn 0.3s ease;
}

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
}

@keyframes slideIn {
    from {
        transform: translateY(-50px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.trophy-modal-close {
    position: absolute;
    top: 1.5rem;
    right: 2rem;
    font-size: 3rem;
    color: var(--color-gold);
    cursor: pointer;
    z-index: 10;
    transition: var(--transition-smooth);
}

