    margin-bottom: 0.5rem;
}

.album-playlist-title {
    font-family: 'Playfair Display', serif;
    font-size: 4rem;
    font-weight: 900;
    color: var(--color-warm-white);
    text-transform: uppercase;
    letter-spacing: 0.02em;
    margin-bottom: 1rem;
    line-height: 1;
}

.album-playlist-artist {
    font-size: 1.3rem;
    font-weight: 600;
    color: var(--color-warm-white);
    margin-bottom: 1rem;
}

.album-playlist-stats {
    font-size: 0.95rem;
    color: rgba(245, 245, 220, 0.7);
}

.album-playlist-stats span {
    margin-right: 0.5rem;
}

.album-playlist-tracks {
    padding: 3rem 0 8rem;
    background: var(--color-dark);
}

.playlist-actions {
    display: flex;
    gap: 1.5rem;
    margin-bottom: 3rem;
    flex-wrap: wrap;
}

.play-all-btn {
    display: inline-flex;
    align-items: center;
    gap: 0.7rem;
    padding: 1rem 2.5rem;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    border: none;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 1rem;
    border-radius: 50px;
    cursor: pointer;
    transition: var(--transition-smooth);
    letter-spacing: 0.05em;
}

.play-all-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 30px rgba(212, 175, 55, 0.6);
}

.play-all-btn i {
    font-size: 1.8rem;
}

.back-to-music-btn {
    display: inline-flex;
    align-items: center;
    gap: 0.7rem;
    padding: 1rem 2.5rem;
    background: transparent;
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 1rem;
    border-radius: 50px;
    transition: var(--transition-smooth);
    letter-spacing: 0.05em;
}

.back-to-music-btn:hover {
    background: var(--color-gold);
    color: var(--color-dark);
}

.back-to-music-btn i {
    font-size: 1.3rem;
}

.spotify-playlist {
    background: rgba(26, 26, 26, 0.4);
    border-radius: 10px;
    padding: 1.5rem;
}

.playlist-header-row {
    display: grid;
    grid-template-columns: 50px 1fr 100px;
    gap: 1rem;
    padding: 1rem 1.5rem;
    border-bottom: 1px solid rgba(212, 175, 55, 0.2);
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.85rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.1em;
}

.playlist-track {
    display: grid;
    grid-template-columns: 50px 1fr 100px;
    gap: 1rem;
    align-items: center;
    padding: 1rem 1.5rem;
    border-radius: 5px;
    transition: var(--transition-smooth);
    position: relative;
}

.playlist-track:hover {
    background: rgba(212, 175, 55, 0.1);
}

.playlist-track .track-num {
    color: rgba(245, 245, 220, 0.6);
    font-weight: 600;
    text-align: center;
}

.playlist-track:hover .track-num {
    display: none;
}

.track-play-btn-spotify {
    position: absolute;
    left: 1.5rem;
    top: 50%;
    transform: translateY(-50%);
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: var(--color-gold);
    border: none;
    color: var(--color-dark);
    cursor: pointer;
    display: none;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    transition: var(--transition-smooth);
}

.playlist-track:hover .track-play-btn-spotify {
    display: flex;
}

.track-play-btn-spotify:hover {
    transform: translateY(-50%) scale(1.1);
}

.track-info-spotify {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.track-cover-mini {
    width: 50px;
    height: 50px;
    border-radius: 5px;
    overflow: hidden;
    flex-shrink: 0;
}

.track-cover-mini img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.track-details {
    flex: 1;
}

.track-name-spotify {
    font-size: 1rem;
    font-weight: 600;
    color: var(--color-warm-white);
    margin-bottom: 0.2rem;
}

.track-artist-spotify {
    font-size: 0.9rem;
    color: rgba(245, 245, 220, 0.6);
}

.playlist-track:hover .track-name-spotify {
    color: var(--color-gold);
}

.track-duration-spotify {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.95rem;
    text-align: right;
}

/* Mobile responsive */
@media (max-width: 768px) {
    .album-playlist-header {
        flex-direction: column;
        align-items: center;
        text-align: center;
    }

    .album-playlist-title {
        font-size: 2.5rem;
    }

    .playlist-header-row {
        grid-template-columns: 40px 1fr 80px;
    }

    .playlist-track {
        grid-template-columns: 40px 1fr 80px;
    }

    .track-play-btn-spotify {
        width: 35px;
        height: 35px;
        font-size: 1.2rem;
    }
}

/* Fix carousel infinite loop - override previous animation */
.carousel-track {
    animation: scroll-carousel-fixed 120s linear infinite;
}

@keyframes scroll-carousel-fixed {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(calc(-350px * 25 - 25rem));
    }
}

.footer-credit {
    text-align: center;
    padding-top: 1.5rem;
    margin-top: 1.5rem;
    border-top: 1px solid rgba(212, 175, 55, 0.08);
}

.footer-credit p {
    font-size: 13px;
    color: #aaa;
    line-height: 1.6;
}

.footer-credit strong {
    color: #c0c0c0;
    font-weight: 600;
}

.video-modal {
    display: none;
    position: fixed;
    z-index: 2000;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.video-modal.active {
    display: flex;
    opacity: 1;
}

.video-modal-content {
    position: relative;
    width: 90%;
    max-width: 1200px;
    background: var(--color-anthracite);
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 10px 50px rgba(212, 175, 55, 0.3);
    animation: modalSlideIn 0.4s ease-out;
}

@keyframes modalSlideIn {
    from {
        transform: translateY(-50px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.video-modal-close {
    position: absolute;
    top: -45px;
    right: 0;
    font-size: 2.5rem;
    color: var(--color-gold);
    cursor: pointer;
    transition: var(--transition-smooth);
    z-index: 2001;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(26, 26, 26, 0.8);
    border-radius: 50%;
}

.video-modal-close:hover {
    color: var(--color-yellow);
    transform: rotate(90deg);
}

.video-wrapper {
    position: relative;
    width: 100%;
    padding-bottom: 56.25%;
    height: 0;
}

.video-wrapper iframe {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

.latest-clip-thumbnail {
    cursor: pointer;
    position: relative;
}

.latest-clip-thumbnail::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0);
    transition: var(--transition-smooth);
}

.latest-clip-thumbnail:hover::after {
    background: rgba(0, 0, 0, 0.2);
}

@media (max-width: 768px) {
    .video-modal-content {
        width: 95%;
    }

    .video-modal-close {
        top: 10px;
        right: 10px;
        background: rgba(26, 26, 26, 0.95);
    }

    .footer-credit p {
        font-size: 12px;
        padding: 0 1rem;
    }
}

/* Cacher les points de pagination des témoignages */
.testimonials-section .slider-dots {
    display: none !important;
}

/* Ajouter de l'espace entre les témoignages et le footer */
#temoignages {
    margin-bottom: 4rem;
}

site-web-statique/css/admin.css.content:
.admin-body {
    margin: 0;
    padding: 0;
    background: var(--color-dark);
    font-family: 'Montserrat', sans-serif;
}

.login-screen {
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, var(--color-dark) 0%, var(--color-anthracite) 100%);
}

.login-container {
    background: rgba(26, 26, 26, 0.95);
    border: 1px solid rgba(212, 175, 55, 0.3);
    border-radius: 15px;
    padding: 3rem;
    max-width: 400px;
    width: 90%;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
}

.login-header {
    text-align: center;
    margin-bottom: 2rem;
}

.login-header h1 {
    font-family: 'Playfair Display', serif;
    color: var(--color-gold);
    font-size: 2.5rem;
    margin-bottom: 0.5rem;
}

.login-header p {
    color: var(--color-warm-white);
    opacity: 0.8;
}

.login-form .form-group {
    margin-bottom: 1.5rem;
}

.login-form label {
    display: block;
    color: var(--color-gold);
    margin-bottom: 0.5rem;
    font-weight: 600;
}

.login-form input {
    width: 100%;
    padding: 0.8rem;
    background: rgba(10, 10, 10, 0.7);
    border: 1px solid rgba(212, 175, 55, 0.3);
    border-radius: 8px;
    color: var(--color-warm-white);
    font-size: 1rem;
    transition: var(--transition-smooth);
}

.login-form input:focus {
    outline: none;
    border-color: var(--color-gold);
    box-shadow: 0 0 0 3px rgba(212, 175, 55, 0.1);
}

.dashboard {
    display: flex;
    min-height: 100vh;
    padding-top: 0;
}

.sidebar {
    width: 260px;
    background: var(--color-anthracite);
    border-right: 1px solid rgba(212, 175, 55, 0.2);
    position: fixed;
    height: 100vh;
    overflow-y: auto;
}

.sidebar-header {
    padding: 2rem;
    border-bottom: 1px solid rgba(212, 175, 55, 0.2);
}

.sidebar-header h2 {
    font-family: 'Playfair Display', serif;
    color: var(--color-gold);
    font-size: 1.8rem;
    margin-bottom: 0.3rem;
}

.sidebar-header p {
    color: rgba(245, 245, 220, 0.7);
    font-size: 0.9rem;
}

.sidebar-nav {
    padding: 1rem 0;
}

.nav-item {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem 2rem;
    color: var(--color-warm-white);
    text-decoration: none;
    transition: var(--transition-smooth);
    cursor: pointer;
}

.nav-item i {
    font-size: 1.5rem;
}

.nav-item:hover {
    background: rgba(212, 175, 55, 0.1);
    color: var(--color-gold);
}

.nav-item.active {
    background: rgba(212, 175, 55, 0.2);
    color: var(--color-gold);
    border-left: 3px solid var(--color-gold);
}

.nav-item.logout {
    color: #ff6b6b;
    margin-top: auto;
}

.nav-item.logout:hover {
    background: rgba(255, 107, 107, 0.1);
}

.main-content {
    margin-left: 260px;
    flex: 1;
    padding: 2rem;
    min-height: 100vh;
}

.section {
    display: none;
}

.section.active {
    display: block;
}

.section-header {
    display: flex;
