    border: 1px solid rgba(212, 175, 55, 0.3);
    border-radius: 15px;
    max-width: 600px;
    width: 100%;
    max-height: 90vh;
    overflow-y: auto;
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 2rem;
    border-bottom: 1px solid rgba(212, 175, 55, 0.2);
}

.modal-header h2 {
    color: var(--color-gold);
    font-size: 1.8rem;
}

.modal-close {
    font-size: 2rem;
    color: var(--color-warm-white);
    cursor: pointer;
    transition: var(--transition-smooth);
}

.modal-close:hover {
    color: var(--color-gold);
}

.modal form {
    padding: 2rem;
}

.modal-footer {
    display: flex;
    justify-content: flex-end;
    gap: 1rem;
    margin-top: 2rem;
}

@media (max-width: 768px) {
    .dashboard {
        flex-direction: column;
    }

    .sidebar {
        position: relative;
        width: 100%;
        height: auto;
    }

    .main-content {
        margin-left: 0;
        padding: 1rem;
    }

    .section-header {
        flex-direction: column;
        gap: 1rem;
        align-items: flex-start;
    }

    .form-row {
        grid-template-columns: 1fr;
    }

    .gallery-grid {
        grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
    }
}

site-web-statique/js/script.js.content:
document.addEventListener('DOMContentLoaded', function() {
    
    const observerOptions = {
        threshold: 0.1,
        rootMargin: '0px 0px -100px 0px'
    };

    const observer = new IntersectionObserver(function(entries) {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('visible');
            }
        });
    }, observerOptions);

    const fadeElements = document.querySelectorAll('.fade-in-scroll');
    fadeElements.forEach(element => {
        observer.observe(element);
    });

    let lastScrollTop = 0;
    window.addEventListener('scroll', function() {
        const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        
        const parallaxBg = document.querySelector('.parallax-bg');
        if (parallaxBg) {
            const offset = scrollTop * 0.5;
            parallaxBg.style.transform = `translateY(${offset}px)`;
        }
        
        lastScrollTop = scrollTop;
    });

    const smoothScrollLinks = document.querySelectorAll('a[href^="#"]');
    smoothScrollLinks.forEach(link => {
        link.addEventListener('click', function(e) {
            const href = this.getAttribute('href');
            if (href !== '#') {
                e.preventDefault();
                const target = document.querySelector(href);
                if (target) {
                    target.scrollIntoView({
                        behavior: 'smooth',
                        block: 'start'
                    });
                }
            }
        });
    });

    const galleryItems = document.querySelectorAll('.gallery-item');
    const lightbox = document.getElementById('lightbox');
    const lightboxImg = document.querySelector('.lightbox-image');
    const lightboxClose = document.querySelector('.lightbox-close');
    const lightboxPrev = document.querySelector('.lightbox-prev');
    const lightboxNext = document.querySelector('.lightbox-next');
    
    let currentImageIndex = 0;
    const galleryImages = Array.from(galleryItems).map(item => item.querySelector('img').src);

    function showLightbox(index) {
        currentImageIndex = index;
        lightboxImg.src = galleryImages[index];
        lightbox.classList.add('active');
        document.body.style.overflow = 'hidden';
    }

    function closeLightbox() {
        lightbox.classList.remove('active');
        document.body.style.overflow = '';
    }

    function showNextImage() {
        currentImageIndex = (currentImageIndex + 1) % galleryImages.length;
        lightboxImg.src = galleryImages[currentImageIndex];
    }

    function showPrevImage() {
        currentImageIndex = (currentImageIndex - 1 + galleryImages.length) % galleryImages.length;
        lightboxImg.src = galleryImages[currentImageIndex];
    }

    if (galleryItems.length > 0 && lightbox && lightboxClose) {
        galleryItems.forEach((item, index) => {
            item.addEventListener('click', () => showLightbox(index));
        });

        lightboxClose.addEventListener('click', closeLightbox);
        lightboxNext.addEventListener('click', showNextImage);
        lightboxPrev.addEventListener('click', showPrevImage);
    }

    if (lightbox) {
        lightbox.addEventListener('click', function(e) {
            if (e.target === lightbox) {
                closeLightbox();
            }
        });
    }

    document.addEventListener('keydown', function(e) {
        if (!lightbox.classList.contains('active')) return;
        
        if (e.key === 'Escape') {
            closeLightbox();
        } else if (e.key === 'ArrowRight') {
            showNextImage();
        } else if (e.key === 'ArrowLeft') {
            showPrevImage();
        }
    });

    const clipCards = document.querySelectorAll('.clip-card');
    clipCards.forEach(card => {
        card.addEventListener('click', function() {
            console.log('Clip clicked:', this.querySelector('.clip-title').textContent);
        });
    });

    const eventButtons = document.querySelectorAll('.event-button');
    eventButtons.forEach(button => {
        button.addEventListener('click', function(e) {
            e.preventDefault();
            const venue = this.closest('.event-card').querySelector('.event-venue').textContent;
            alert(`Réservation pour: ${venue}\n\nCette fonctionnalité sera bientôt disponible!`);
        });
    });

    const mobileMenuToggle = document.querySelector('.mobile-menu-toggle');
    const navMenu = document.querySelector('.nav-menu');
    
    if (mobileMenuToggle) {
        mobileMenuToggle.addEventListener('click', function() {
            navMenu.classList.toggle('active');
            mobileMenuToggle.classList.toggle('active');
        });

        document.querySelectorAll('.nav-link').forEach(link => {
            link.addEventListener('click', () => {
                navMenu.classList.remove('active');
                mobileMenuToggle.classList.remove('active');
            });
        });
    }

    const testimonialsSlider = document.querySelector('.testimonials-slider');
    if (testimonialsSlider) {
        const track = testimonialsSlider.querySelector('.testimonials-track');
        const slides = testimonialsSlider.querySelectorAll('.testimonial-card');
        const prevBtn = testimonialsSlider.querySelector('.slider-nav.prev');
        const nextBtn = testimonialsSlider.querySelector('.slider-nav.next');
        const dotsContainer = testimonialsSlider.querySelector('.slider-dots');
        
        let currentSlide = 0;
        let autoSlideInterval;
        
        slides.forEach((_, index) => {
            const dot = document.createElement('div');
            dot.classList.add('slider-dot');
            if (index === 0) dot.classList.add('active');
            dot.addEventListener('click', () => goToSlide(index));
            dotsContainer.appendChild(dot);
        });
        
        const dots = dotsContainer.querySelectorAll('.slider-dot');
        
        function updateSlider() {
            track.style.transform = `translateX(-${currentSlide * 100}%)`;
            dots.forEach((dot, index) => {
                dot.classList.toggle('active', index === currentSlide);
            });
        }
        
        function goToSlide(index) {
            currentSlide = index;
            updateSlider();
            resetAutoSlide();
        }
        
        function nextSlide() {
            currentSlide = (currentSlide + 1) % slides.length;
            updateSlider();
        }
        
        function prevSlide() {
            currentSlide = (currentSlide - 1 + slides.length) % slides.length;
            updateSlider();
        }
        
        function resetAutoSlide() {
            clearInterval(autoSlideInterval);
            autoSlideInterval = setInterval(nextSlide, 5000);
        }
        
        prevBtn.addEventListener('click', () => {
            prevSlide();
            resetAutoSlide();
        });
        
        nextBtn.addEventListener('click', () => {
            nextSlide();
            resetAutoSlide();
        });
        
        autoSlideInterval = setInterval(nextSlide, 5000);
    }

    const trackItems = document.querySelectorAll('.track-item');
    const nowPlayingCover = document.querySelector('.now-playing-cover img');
    const trackName = document.querySelector('.track-name');
    const artistName = document.querySelector('.artist-name');

    trackItems.forEach(track => {
        const playBtn = track.querySelector('.track-play-btn');
        
        if (playBtn) {
            playBtn.addEventListener('click', function(e) {
                e.stopPropagation();
                const title = track.getAttribute('data-track-title');
                const album = track.getAttribute('data-track-album');
                
                if (trackName && artistName) {
                    trackName.textContent = title;
                    artistName.textContent = 'Axel Merryl';
                }

                trackItems.forEach(t => t.classList.remove('playing'));
                track.classList.add('playing');
            });
        }

        track.addEventListener('click', function() {
            const title = this.getAttribute('data-track-title');
            const album = this.getAttribute('data-track-album');
            
            if (trackName && artistName) {
                trackName.textContent = title;
                artistName.textContent = 'Axel Merryl';
            }

            trackItems.forEach(t => t.classList.remove('playing'));
            this.classList.add('playing');
        });
    });
});

    // Trophy Modal Functionality
    const trophyModal = document.getElementById('trophyModal');
    const trophyBtns = document.querySelectorAll('.trophy-details-btn');
    const modalClose = document.querySelector('.trophy-modal-close');
    const modalTitle = document.getElementById('modal-title');
    const modalBody = document.getElementById('modal-body');

    // Trophy data for each award
    const trophyData = {
        bsa: {
            title: 'BÉNIN SHOWBIZ AWARDS',
            categories: [
                {
                    title: 'BSA d\'Or (Meilleur Artiste de l\'Année)',
                    year: '2023',
                    description: 'Décerné à Axel Merryl pour son tube « Kimi » lors de la cérémonie du 15 décembre 2023.',
                    image: 'attached_assets/stock_images/trophé 1.jpeg'
                },
                {
                    title: 'Meilleur Clip de l\'Année',
                    year: '2023',
                    description: 'Remis au clip de « Kimi », une production visuelle exceptionnelle qui a marqué l\'année.',
                    image: 'attached_assets/stock_images/trophé 2.jpeg'
                },
                {
                    title: 'Meilleur Single de l\'Année',
                    year: '2023',
                    description: 'Le single « Kimi » a été sacré meilleur titre de l\'année pour son impact musical.',
                    image: 'attached_assets/stock_images/trophé3.jpeg'
                }
            ]
        },
        arawo: {
            title: 'ÀRÀWÒ AMBASSADEUR AWARDS',
            categories: [
                {
                    title: 'Meilleur Artiste Masculin Bénin',
                    year: '2025',
                    description: 'Lors de la 5ᵉ édition des Ambassadeur Awards à Cotonou, Axel Merryl a été sacré meilleur artiste masculin du Bénin.',
                    image: 'attached_assets/stock_images/trophé 4.jpeg'
                },
                {
                    title: 'Meilleure Collaboration de l\'Année',
                    year: '2025',
                    description: 'Prix de la meilleure collaboration pour son duo avec le groupe Toofan sur le single « Gba Gba », sorti en avril 2025.',
                    image: 'attached_assets/stock_images/trophé 5.jpeg'
                }
            ]
        },
        primud: {
            title: 'PRIMUD - PRIX INTERNATIONAUX DE LA MUSIQUE URBAINE',
            categories: [
                {
                    title: 'Meilleur Artiste de l\'Afrique de l\'Ouest',
                    year: '2024',
                    description: 'Décerné à Axel Merryl lors de la 9ᵉ édition des PRIMUD à Abidjan (10 novembre 2024), reconnaissant son influence dans la région.',
                    image: 'attached_assets/stock_images/axel-merryl-primud-meilleur-artiste-afrique-de-louest.jpg'
                }
            ]
        }
    };

    function openTrophyModal(awardKey) {
        const data = trophyData[awardKey];
        if (!data) return;

        modalTitle.textContent = data.title;

        let categoriesHTML = '<div class="trophy-category-grid">';
        data.categories.forEach(category => {
            categoriesHTML += `
                <div class="trophy-category-card">
                    <img src="${category.image}" alt="${category.title}" loading="lazy" />
                    <h4>${category.title}</h4>
                    <p class="trophy-year">${category.year}</p>
                    <p>${category.description}</p>
                </div>
            `;
        });
        categoriesHTML += '</div>';

        modalBody.innerHTML = categoriesHTML;
        trophyModal.classList.add('active');
        document.body.style.overflow = 'hidden';
    }

    function closeTrophyModal() {
        trophyModal.classList.remove('active');
        document.body.style.overflow = '';
    }

    if (trophyBtns.length > 0 && trophyModal) {
        trophyBtns.forEach(btn => {
            btn.addEventListener('click', function() {
                const awardKey = this.getAttribute('data-award');
                openTrophyModal(awardKey);
            });
        });

        modalClose.addEventListener('click', closeTrophyModal);

        trophyModal.addEventListener('click', function(e) {
            if (e.target === trophyModal) {
                closeTrophyModal();
            }
        });

        document.addEventListener('keydown', function(e) {
            if (e.key === 'Escape' && trophyModal.classList.contains('active')) {
                closeTrophyModal();
            }
        });
    }

    // Duplicate carousel slides for seamless infinite loop
    const carouselTrack = document.querySelector('.carousel-track');
    if (carouselTrack) {
        const slides = carouselTrack.querySelectorAll('.carousel-slide');
        slides.forEach(slide => {
            const clone = slide.cloneNode(true);
            carouselTrack.appendChild(clone);
        });
    }

    // Video Modal Functionality
    const videoModal = document.getElementById('videoModal');
    const videoPlayer = document.getElementById('youtubePlayer');
    const videoModalClose = document.querySelector('.video-modal-close');

    function extractYouTubeID(url) {
        const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|\&v=)([^#\&\?]*).*/;
        const match = url.match(regExp);
        return (match && match[2].length === 11) ? match[2] : null;
    }

    function openVideoModal(videoUrl) {
        const videoId = extractYouTubeID(videoUrl);
        if (videoId) {
            videoPlayer.src = `https://www.youtube.com/embed/${videoId}?autoplay=1&rel=0`;
            videoModal.classList.add('active');
            document.body.style.overflow = 'hidden';
        }
    }

    function closeVideoModal() {
        videoPlayer.src = '';
        videoModal.classList.remove('active');
        document.body.style.overflow = '';
    }

    const latestClipCards = document.querySelectorAll('.latest-clip-card');
    latestClipCards.forEach(card => {
        const thumbnail = card.querySelector('.latest-clip-thumbnail');
        const watchButton = card.querySelector('.watch-button');

        if (thumbnail && watchButton) {
            const videoUrl = watchButton.getAttribute('href');

            thumbnail.addEventListener('click', function(e) {
                e.preventDefault();
                openVideoModal(videoUrl);
            });

            watchButton.addEventListener('click', function(e) {
                e.preventDefault();
                openVideoModal(videoUrl);
            });
        }
    });

    if (videoModalClose) {
        videoModalClose.addEventListener('click', closeVideoModal);
    }

    if (videoModal) {
        videoModal.addEventListener('click', function(e) {
            if (e.target === videoModal) {
                closeVideoModal();
            }
        });
    }

    document.addEventListener('keydown', function(e) {
        if (e.key === 'Escape' && videoModal && videoModal.classList.contains('active')) {
            closeVideoModal();
        }
    });

    async function loadDynamicEvents() {
        try {
            const response = await fetch('/api/events');
            const events = await response.json();

            const eventsList = document.querySelector('.events-list');
            if (!eventsList) return;

            if (events.length === 0) {
                eventsList.innerHTML = '<p style="color: rgba(245, 245, 220, 0.6); text-align: center; padding: 2rem;">Aucun événement prévu pour le moment</p>';
                return;
            }

            eventsList.innerHTML = events.map(event => {
                const eventDate = new Date(event.date);
                const day = eventDate.getDate();
                const monthNames = ['JAN', 'FÉV', 'MAR', 'AVR', 'MAI', 'JUN', 'JUL', 'AOU', 'SEP', 'OCT', 'NOV', 'DÉC'];
                const month = monthNames[eventDate.getMonth()];
                const year = eventDate.getFullYear();

                return `
                    <div class="event-card">
                        <div class="event-date">
                            <span class="date-day">${day}</span>
                            <span class="date-month">${month} ${year}</span>
                        </div>
                        <div class="event-info">
                            <h3 class="event-venue">${event.location} - ${event.name}</h3>
                            <p class="event-time">${event.time}</p>
                            ${event.description ? `<p class="event-description" style="margin-top: 0.5rem; color: rgba(245, 245, 220, 0.8); font-size: 0.95rem;">${event.description}</p>` : ''}
                        </div>
                        ${event.ticket_link ? 
                            `<a href="${event.ticket_link}" target="_blank" rel="noopener noreferrer" class="event-button">Réserver</a>` :
                            `<a href="#" class="event-button" onclick="alert('Billetterie bientôt disponible'); return false;">Réserver</a>`
                        }
                    </div>
                `;
            }).join('');
        } catch (error) {
            console.error('Error loading events:', error);
        }
    }

    async function loadDynamicGallery() {
        try {
            const response = await fetch('/api/gallery');
            const images = await response.json();

            const carouselTrack = document.querySelector('.carousel-track');
            if (!carouselTrack) return;

            if (images.length === 0) {
                return;
            }

            const doubleImages = [...images, ...images];

            carouselTrack.innerHTML = doubleImages.map(image => `
                <div class="carousel-slide">
                    <img src="attached_assets/stock_images/${image.filename}" alt="${image.alt_text}" loading="lazy" />
                </div>
            `).join('');
        } catch (error) {
            console.error('Error loading gallery:', error);
        }
    }

    loadDynamicEvents();
    loadDynamicGallery();

site-web-statique/js/fixes.js.content:
document.addEventListener('DOMContentLoaded', function() {

    // Fix pour la galerie carousel
    const carouselSlides = document.querySelectorAll('.carousel-slide');
    const lightbox = document.getElementById('lightbox');
    const lightboxImg = document.querySelector('.lightbox-image');

    if (carouselSlides.length > 0 && lightbox && lightboxImg) {
        const uniqueSlides = Array.from(carouselSlides).slice(0, Math.floor(carouselSlides.length / 2));
        const galleryImages = uniqueSlides.map(item => item.querySelector('img').src);
        let currentImageIndex = 0;

        function showCarouselLightbox(index) {
            currentImageIndex = index;
            if (galleryImages[index]) {
                lightboxImg.src = galleryImages[index];
                lightbox.classList.add('active');
                document.body.style.overflow = 'hidden';
            }
        }

        uniqueSlides.forEach((item, index) => {
            item.style.cursor = 'pointer';
            item.addEventListener('click', (e) => {
                e.stopPropagation();
                showCarouselLightbox(index);
            });
        });
    }
});

css/hero-parallax.css.content:
/* HERO CINÉMATIQUE - Animations premium */

.hero-section {
    position: relative;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}

/* Image de fond fixe */
.hero-fixed-bg {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
}

.hero-fixed-bg img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
}

/* Overlay avec gradient élégant */
.hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
        135deg,
        rgba(10, 10, 10, 0.85) 0%,
        rgba(26, 26, 26, 0.7) 50%,
        rgba(10, 10, 10, 0.85) 100%
    );
    pointer-events: none;
}

/* Conteneur du contenu */
.hero-content {
    position: relative;
    z-index: 2;
    text-align: center;
    padding: 2rem;
}

/* Titre principal - Arrive du HAUT */
.hero-title {
    font-family: 'Playfair Display', serif;
    font-size: clamp(3rem, 10vw, 8rem);
    font-weight: 900;
    color: var(--color-gold);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    margin-bottom: 1rem;
    text-shadow: 0 4px 20px rgba(212, 175, 55, 0.5);
    opacity: 0;
    transform: translateY(-100px);
}

.hero-slide-down {
    animation: slideDown 1.2s cubic-bezier(0.16, 1, 0.3, 1) 0.3s forwards;
}

@keyframes slideDown {
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Sous-titre - Arrive de la GAUCHE */
.hero-subtitle {
    font-size: clamp(1.2rem, 3vw, 2rem);
    font-weight: 300;
    color: var(--color-warm-white);
    letter-spacing: 0.3em;
    margin-bottom: 3rem;
    text-transform: uppercase;
    opacity: 0;
    transform: translateX(-80px);
}

.hero-slide-left {
    animation: slideLeft 1s cubic-bezier(0.16, 1, 0.3, 1) 0.8s forwards;
}

@keyframes slideLeft {
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

/* Boutons - Arrivent du BAS */
.hero-buttons {
    display: flex;
    gap: 1.5rem;
    justify-content: center;
    flex-wrap: wrap;
    opacity: 0;
    transform: translateY(60px);
}

.hero-slide-up {
    animation: slideUp 1s cubic-bezier(0.16, 1, 0.3, 1) 1.2s forwards;
}

@keyframes slideUp {
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Styles des boutons */
.cta-button {
    display: inline-block;
    padding: 1rem 3rem;
    background: linear-gradient(
        135deg,
        var(--color-gold) 0%,
        var(--color-yellow) 100%
    );
    color: var(--color-dark);
    text-decoration: none;
    font-weight: 700;
    font-size: 1.1rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    border-radius: 50px;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 0 4px 20px rgba(212, 175, 55, 0.4);
}

.cta-button:hover {
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 8px 30px rgba(212, 175, 55, 0.6);
}

/* Indicateur de scroll */
.scroll-indicator {
    position: absolute;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
    z-index: 3;
    opacity: 0;
    animation: fadeInScroll 1s ease 2s forwards;
}

@keyframes fadeInScroll {
    to {
        opacity: 1;
    }
}

.scroll-indicator span {
    display: block;
    width: 2px;
    height: 40px;
    background: linear-gradient(
        to bottom,
        transparent,
        var(--color-gold)
    );
    animation: scrollBounce 2s ease-in-out infinite;
}

@keyframes scrollBounce {
    0%, 100% {
        transform: translateY(0);
        opacity: 0.6;
    }
    50% {
        transform: translateY(10px);
        opacity: 1;
    }
}

/* Responsive */
@media (max-width: 768px) {
    .hero-title {
        transform: translateY(-60px);
    }

    .hero-subtitle {
        transform: translateX(-50px);
    }

    .hero-buttons {
        transform: translateY(40px);
    }
}

/* Accessibilité - Respecte prefers-reduced-motion */
@media (prefers-reduced-motion: reduce) {
    .hero-slide-down,
    .hero-slide-left,
    .hero-slide-up {
        animation: none;
        opacity: 1;
        transform: none;
    }

    .scroll-indicator span {
        animation: none;
    }
}

js/hero-parallax.js.content:
// HERO CINÉMATIQUE - Animations premium au chargement
(function() {
    'use strict';

    const heroSection = document.querySelector('.hero-section');
    if (!heroSection) return;

    // Ajoute une classe pour indiquer que les animations sont prêtes
    window.addEventListener('load', function() {
        heroSection.classList.add('hero-loaded');
    });

    // Animation de l'indicateur de scroll (apparaît après les autres animations)
    const scrollIndicator = heroSection.querySelector('.scroll-indicator');
    if (scrollIndicator) {
        setTimeout(() => {
            scrollIndicator.style.opacity = '1';
        }, 2000);
    }

    // Effet supplémentaire : léger mouvement de parallax au scroll (optionnel, très subtil)
    let ticking = false;

    window.addEventListener('scroll', function() {
        if (!ticking) {
            window.requestAnimationFrame(function() {
                const scrolled = window.pageYOffset;
                const heroContent = heroSection.querySelector('.hero-content');

                if (heroContent && scrolled < window.innerHeight) {
                    // Parallax très subtil au scroll
                    const offset = scrolled * 0.3;
                    heroContent.style.transform = `translateY(${offset}px)`;
                    heroContent.style.opacity = 1 - (scrolled / window.innerHeight) * 0.5;
                }

                ticking = false;
            });

            ticking = true;
        }
    });

    console.log('HERO Cinématique initialisé');
})();

site-web-statique/js/admin.js.content:
const API_BASE = window.location.origin + '/api';

const loginScreen = document.getElementById('login-screen');
const dashboard = document.getElementById('dashboard');
const loginForm = document.getElementById('login-form');
const loginError = document.getElementById('login-error');

const navItems = document.querySelectorAll('.nav-item[data-section]');
const sections = document.querySelectorAll('.section');
const logoutBtn = document.getElementById('logout-btn');

const eventModal = document.getElementById('event-modal');
const galleryModal = document.getElementById('gallery-modal');
const eventForm = document.getElementById('event-form');
const galleryForm = document.getElementById('gallery-form');

let currentEventId = null;

async function checkAuth() {
    try {
        const response = await fetch(`${API_BASE}/check-auth`, {
            credentials: 'include'
        });
        const data = await response.json();

        if (data.authenticated) {
            showDashboard();
        } else {
            showLogin();
        }
    } catch (error) {
        console.error('Auth check error:', error);
        showLogin();
    }
}

function showLogin() {
    loginScreen.style.display = 'flex';
    dashboard.style.display = 'none';
}

function showDashboard() {
    loginScreen.style.display = 'none';
    dashboard.style.display = 'flex';
    loadEvents();
    loadGallery();
}

loginForm.addEventListener('submit', async (e) => {
    e.preventDefault();

    const password = document.getElementById('password').value;

    try {
        const response = await fetch(`${API_BASE}/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            credentials: 'include',
            body: JSON.stringify({ password })
        });

        const data = await response.json();

        if (data.success) {
            showDashboard();
        } else {
            loginError.textContent = data.error || 'Mot de passe incorrect';
            loginError.classList.add('show');
        }
    } catch (error) {
        loginError.textContent = 'Erreur de connexion';
        loginError.classList.add('show');
    }
});

logoutBtn.addEventListener('click', async (e) => {
    e.preventDefault();

    try {
        await fetch(`${API_BASE}/logout`, {
            method: 'POST',
            credentials: 'include'
        });

        showLogin();
    } catch (error) {
        console.error('Logout error:', error);
    }
});

navItems.forEach(item => {
    item.addEventListener('click', (e) => {
        e.preventDefault();

        const sectionId = item.dataset.section;

        navItems.forEach(nav => nav.classList.remove('active'));
        item.classList.add('active');

        sections.forEach(section => section.classList.remove('active'));
        document.getElementById(`${sectionId}-section`).classList.add('active');
    });
});

document.getElementById('add-event-btn').addEventListener('click', () => {
    currentEventId = null;
    document.getElementById('modal-title').textContent = 'Nouvel Événement';
    eventForm.reset();
    eventModal.classList.add('show');
});

document.getElementById('add-gallery-btn').addEventListener('click', () => {
    galleryForm.reset();
    galleryModal.classList.add('show');
});

document.querySelectorAll('.modal-close, .modal-cancel').forEach(btn => {
    btn.addEventListener('click', () => {
        eventModal.classList.remove('show');
        galleryModal.classList.remove('show');
    });
});

window.addEventListener('click', (e) => {
    if (e.target === eventModal) {
        eventModal.classList.remove('show');
    }
    if (e.target === galleryModal) {
        galleryModal.classList.remove('show');
    }
});

async function loadEvents() {
    try {
        const response = await fetch(`${API_BASE}/events`);
        const events = await response.json();

        const eventsList = document.getElementById('events-list');

        if (events.length === 0) {
            eventsList.innerHTML = '<p style="color: rgba(245, 245, 220, 0.6); text-align: center; padding: 2rem;">Aucun événement pour le moment</p>';
            return;
        }

        eventsList.innerHTML = events.map(event => `
            <div class="event-item">
                <div class="event-item-header">
                    <div>
                        <h3 class="event-item-title">${event.name}</h3>
                    </div>
                    <div class="event-item-actions">
                        <button class="btn-icon" onclick="editEvent(${event.id})">
                            <i class='bx bx-edit'></i>
                        </button>
                        <button class="btn-icon danger" onclick="deleteEvent(${event.id})">
                            <i class='bx bx-trash'></i>
                        </button>
                    </div>
                </div>
                <div class="event-item-info">
                    <p><strong>Date:</strong> ${formatDate(event.date)}</p>
                    <p><strong>Heure:</strong> ${event.time}</p>
                    <p><strong>Lieu:</strong> ${event.location}</p>
                    ${event.description ? `<p><strong>Description:</strong> ${event.description}</p>` : ''}
                    ${event.ticket_link ? `<p><strong>Réservation:</strong> <a href="${event.ticket_link}" target="_blank" style="color: var(--color-gold);">Lien</a></p>` : ''}
                </div>
            </div>
        `).join('');
    } catch (error) {
        console.error('Error loading events:', error);
    }
}

window.editEvent = async function(id) {
    try {
        const response = await fetch(`${API_BASE}/events`);
        const events = await response.json();
        const event = events.find(e => e.id === id);

        if (!event) return;

        currentEventId = id;
        document.getElementById('modal-title').textContent = 'Modifier Événement';
        document.getElementById('event-id').value = id;
        document.getElementById('event-name').value = event.name;
        document.getElementById('event-date').value = event.date;
        document.getElementById('event-time').value = event.time;
        document.getElementById('event-location').value = event.location;
        document.getElementById('event-image').value = event.image || '';
        document.getElementById('event-description').value = event.description || '';
        document.getElementById('event-ticket-link').value = event.ticket_link || '';

        eventModal.classList.add('show');
    } catch (error) {
        console.error('Error loading event:', error);
    }
};

window.deleteEvent = async function(id) {
    if (!confirm('Êtes-vous sûr de vouloir supprimer cet événement ?')) {
        return;
    }

    try {
        const response = await fetch(`${API_BASE}/events/${id}`, {
            method: 'DELETE',
            credentials: 'include'
        });

        if (response.ok) {
            loadEvents();
        }
    } catch (error) {
        console.error('Error deleting event:', error);
    }
};

eventForm.addEventListener('submit', async (e) => {
    e.preventDefault();

    const eventData = {
        name: document.getElementById('event-name').value,
        date: document.getElementById('event-date').value,
        time: document.getElementById('event-time').value,
        location: document.getElementById('event-location').value,
        image: document.getElementById('event-image').value,
        description: document.getElementById('event-description').value,
        ticket_link: document.getElementById('event-ticket-link').value
    };

    try {
        const url = currentEventId 
            ? `${API_BASE}/events/${currentEventId}`
            : `${API_BASE}/events`;
        const method = currentEventId ? 'PUT' : 'POST';

        const response = await fetch(url, {
            method,
            headers: {
                'Content-Type': 'application/json'
            },
            credentials: 'include',
            body: JSON.stringify(eventData)
        });

        if (response.ok) {
            eventModal.classList.remove('show');
            loadEvents();
        }
    } catch (error) {
        console.error('Error saving event:', error);
    }
});

async function loadGallery() {
    try {
        const response = await fetch(`${API_BASE}/gallery`);
        const images = await response.json();

        const galleryList = document.getElementById('gallery-list');

        if (images.length === 0) {
            galleryList.innerHTML = '<p style="color: rgba(245, 245, 220, 0.6); text-align: center; padding: 2rem; grid-column: 1/-1;">Aucune image dans la galerie</p>';
            return;
        }

        galleryList.innerHTML = images.map(image => `
            <div class="gallery-item">
                <img src="attached_assets/stock_images/${image.filename}" 
                     alt="${image.alt_text}" 
                     class="gallery-item-image"
                     onerror="this.src='data:image/svg+xml,<svg xmlns=%22http://www.w3.org/2000/svg%22 width=%22200%22 height=%22200%22><rect fill=%22%23333%22 width=%22200%22 height=%22200%22/><text fill=%22%23666%22 x=%2250%%22 y=%2250%%22 text-anchor=%22middle%22 dy=%22.3em%22>Image non trouvée</text></svg>'">
                <div class="gallery-item-actions">
                    <span class="gallery-item-name">${image.filename}</span>
                    <button class="btn-icon danger" onclick="deleteGalleryImage(${image.id})">
                        <i class='bx bx-trash'></i>
                    </button>
                </div>
            </div>
        `).join('');
    } catch (error) {
        console.error('Error loading gallery:', error);
    }
}

window.deleteGalleryImage = async function(id) {
    if (!confirm('Êtes-vous sûr de vouloir supprimer cette image ?')) {
        return;
    }

    try {
        const response = await fetch(`${API_BASE}/gallery/${id}`, {
            method: 'DELETE',
            credentials: 'include'
        });

        if (response.ok) {
            loadGallery();
        }
    } catch (error) {
        console.error('Error deleting image:', error);
    }
};

galleryForm.addEventListener('submit', async (e) => {
    e.preventDefault();

    const imageData = {
        filename: document.getElementById('gallery-filename').value,
        alt_text: document.getElementById('gallery-alt').value
    };

    try {
        const response = await fetch(`${API_BASE}/gallery`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            credentials: 'include',
            body: JSON.stringify(imageData)
        });

        if (response.ok) {
            galleryModal.classList.remove('show');
            loadGallery();
        }
    } catch (error) {
        console.error('Error adding image:', error);
    }
});

const changePasswordForm = document.getElementById('change-password-form');
const passwordError = document.getElementById('password-error');
const passwordSuccess = document.getElementById('password-success');

changePasswordForm.addEventListener('submit', async (e) => {
    e.preventDefault();

    passwordError.classList.remove('show');
    passwordSuccess.classList.remove('show');

    const oldPassword = document.getElementById('old-password').value;
    const newPassword = document.getElementById('new-password').value;
    const confirmPassword = document.getElementById('confirm-password').value;

    if (newPassword !== confirmPassword) {
        passwordError.textContent = 'Les mots de passe ne correspondent pas';
        passwordError.classList.add('show');
        return;
    }

    if (newPassword.length < 6) {
        passwordError.textContent = 'Le nouveau mot de passe doit contenir au moins 6 caractères';
        passwordError.classList.add('show');
        return;
    }

    try {
        const response = await fetch(`${API_BASE}/change-password`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            credentials: 'include',
            body: JSON.stringify({
                old_password: oldPassword,
                new_password: newPassword
            })
        });

        const data = await response.json();

        if (response.ok) {
            passwordSuccess.textContent = 'Mot de passe modifié avec succès !';
            passwordSuccess.classList.add('show');
            changePasswordForm.reset();
        } else {
            passwordError.textContent = data.error || 'Erreur lors du changement de mot de passe';
            passwordError.classList.add('show');
        }
    } catch (error) {
        passwordError.textContent = 'Erreur de connexion';
        passwordError.classList.add('show');
    }
});

function formatDate(dateString) {
    const date = new Date(dateString);
    const options = { day: 'numeric', month: 'long', year: 'numeric' };
    return date.toLocaleDateString('fr-FR', options);
}

checkAuth();

site-web-statique/css/mobile-fix.css.content:
/* Fix horizontal scroll on mobile */
html {
    overflow-x: hidden !important;
    width: 100%;
}

body {
    overflow-x: hidden !important;
    width: 100%;
    max-width: 100vw;
    position: relative;
}

/* Permet au carousel de fonctionner tout en évitant le scroll horizontal */
.carousel-gallery-section {
    overflow-x: hidden !important;
}

.carousel-container {
    overflow: hidden !important;
}

/* Le carousel-track doit pouvoir déborder pour l'animation */
.carousel-track {
    max-width: none !important;
}

/* Fix pour mobile */
@media (max-width: 768px) {
    .container,
    .container-full {
        padding: 0 1rem;
    }

    .hero-content {
        padding: 1rem;
    }

    .carousel-container::before,
    .carousel-container::after {
        display: none;
    }

    .carousel-slide {
        width: 250px !important;
        height: 180px !important;
        margin: 0 0.5rem;
    }

    .latest-clips-grid {
        padding: 0 0.5rem;
    }

    .bio-grid {
        padding: 0;
    }

    .events-list {
        padding: 0;
    }
}

@media (max-width: 480px) {
    .container,
    .container-full {
        padding: 0 0.75rem;
    }

    .carousel-slide {
        width: 220px !important;
        height: 160px !important;
        margin: 0 0.25rem;
    }
}

/* Force l'animation infinie du carousel */
.carousel-track {
    animation: scroll-carousel 120s linear infinite !important;
}

@keyframes scroll-carousel {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(-50%);
    }
}

.carousel-track:hover {
    animation-play-state: paused !important;
}

/* Fix pour que les images du carousel ne soient pas coupées */
.carousel-slide img {
    object-fit: contain !important;
    background: rgba(26, 26, 26, 0.3);
}

.carousel-slide {
    background: rgba(10, 10, 10, 0.5);
}

site-web-statique/css/style.css.content:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --color-dark: #0a0a0a;
    --color-anthracite: #1a1a1a;
    --color-gold: #d4af37;
    --color-yellow: #f4d03f;
    --color-warm-white: #f5f5dc;
    --color-overlay: rgba(10, 10, 10, 0.7);
    --transition-smooth: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Montserrat', sans-serif;
    background-color: var(--color-dark);
    color: var(--color-warm-white);
    line-height: 1.6;
    overflow-x: hidden;
    padding-top: 80px;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
}

.container-full {
    width: 100%;
    padding: 0 2rem;
}

.hero-section {
    position: relative;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}

.hero-image {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
}

.hero-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.parallax-bg {
    will-change: transform;
}

.hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
        135deg,
        rgba(10, 10, 10, 0.8) 0%,
        rgba(26, 26, 26, 0.6) 100%
    );
}

.hero-content {
    position: relative;
    z-index: 2;
    text-align: center;
    padding: 2rem;
}

.hero-title {
    font-family: 'Playfair Display', serif;
    font-size: clamp(3rem, 10vw, 8rem);
    font-weight: 900;
    color: var(--color-gold);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    margin-bottom: 1rem;
    text-shadow: 0 4px 20px rgba(212, 175, 55, 0.5);
}

.hero-subtitle {
    font-size: clamp(1.2rem, 3vw, 2rem);
    font-weight: 300;
    color: var(--color-warm-white);
    letter-spacing: 0.3em;
    margin-bottom: 3rem;
    text-transform: uppercase;
}

.cta-button {
    display: inline-block;
    padding: 1rem 3rem;
    background: linear-gradient(
        135deg,
        var(--color-gold) 0%,
        var(--color-yellow) 100%
    );
    color: var(--color-dark);
    text-decoration: none;
    font-weight: 700;
    font-size: 1.1rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    border-radius: 50px;
    transition: var(--transition-smooth);
    box-shadow: 0 4px 20px rgba(212, 175, 55, 0.4);
}

.cta-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 30px rgba(212, 175, 55, 0.6);
}

.hero-buttons {
    display: flex;
    gap: 1.5rem;
    justify-content: center;
    flex-wrap: wrap;
}

.cta-button-secondary {
    display: inline-block;
    padding: 0.8rem 2rem;
    background: transparent;
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    border-radius: 50px;
    transition: var(--transition-smooth);
    margin-top: 1rem;
}

.cta-button-secondary:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
}

.text-center {
    text-align: center;
}

.main-header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background: rgba(10, 10, 10, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid rgba(212, 175, 55, 0.1);
    z-index: 1000;
    transition: var(--transition-smooth);
}

.main-nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 0;
}

.logo {
    text-decoration: none;
}

.logo-text {
    font-family: 'Playfair Display', serif;
    font-size: 1.8rem;
    font-weight: 900;
    color: var(--color-gold);
    letter-spacing: 0.05em;
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: 2rem;
    margin: 0;
}

.nav-link {
    color: var(--color-warm-white);
    text-decoration: none;
    font-weight: 600;
    text-transform: uppercase;
    font-size: 0.9rem;
    letter-spacing: 0.05em;
    transition: var(--transition-smooth);
    position: relative;
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, var(--color-gold), var(--color-yellow));
    transition: var(--transition-smooth);
}

.nav-link:hover,
.nav-link.active {
    color: var(--color-gold);
}

.nav-link:hover::after,
.nav-link.active::after {
    width: 100%;
}

.mobile-menu-toggle {
    display: none;
    flex-direction: column;
    gap: 4px;
    background: none;
    border: none;
    cursor: pointer;
    padding: 0.5rem;
}

.mobile-menu-toggle span {
    width: 25px;
    height: 3px;
    background: var(--color-gold);
    border-radius: 2px;
    transition: var(--transition-smooth);
}

.scroll-indicator {
    position: absolute;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
    z-index: 2;
}

.scroll-indicator span {
    display: block;
    width: 30px;
    height: 50px;
    border: 2px solid var(--color-gold);
    border-radius: 25px;
    position: relative;
}

.scroll-indicator span::before {
    content: '';
    position: absolute;
    top: 10px;
    left: 50%;
    width: 6px;
    height: 6px;
    background: var(--color-gold);
    border-radius: 50%;
    transform: translateX(-50%);
    animation: scroll-down 2s infinite;
}

@keyframes scroll-down {
    0%,
    20% {
        transform: translateX(-50%) translateY(0);
        opacity: 1;
    }
    100% {
        transform: translateX(-50%) translateY(20px);
        opacity: 0;
    }
}

.bio-section,
.clips-section,
.events-section,
.gallery-section {
    padding: 8rem 0;
    position: relative;
}

.bio-section {
    background: linear-gradient(
        180deg,
        var(--color-dark) 0%,
        var(--color-anthracite) 100%
    );
}

.bio-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: center;
}

.bio-image {
    position: relative;
    overflow: hidden;
    border-radius: 10px;
    box-shadow: 0 20px 60px rgba(212, 175, 55, 0.2);
}

.bio-image img {
    width: 100%;
    height: auto;
    display: block;
    transition: var(--transition-smooth);
}

.bio-image:hover img {
    transform: scale(1.05);
}

.section-title {
    font-family: 'Playfair Display', serif;
    font-size: clamp(2.5rem, 5vw, 4rem);
    font-weight: 900;
    color: var(--color-gold);
    margin-bottom: 2rem;
    position: relative;
    display: inline-block;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 0;
    width: 60px;
    height: 4px;
    background: linear-gradient(90deg, var(--color-gold), var(--color-yellow));
}

.section-title.centered {
    display: block;
    text-align: center;
}

.section-title.centered::after {
    left: 50%;
    transform: translateX(-50%);
}

.bio-text {
    font-size: 1.1rem;
    line-height: 1.8;
}

.highlight-text {
    font-size: 1.4rem;
    font-style: italic;
    color: var(--color-gold);
    margin-bottom: 1.5rem;
    line-height: 1.6;
}

.clips-section {
    background: var(--color-anthracite);
}

.clips-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
    margin-top: 4rem;
}

.clip-card {
    position: relative;
    height: 350px;
    border-radius: 10px;
    overflow: hidden;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.clip-image {
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    transition: var(--transition-smooth);
}

.clip-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.clip-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(10, 10, 10, 0.7);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: var(--transition-smooth);
}

.clip-card:hover .clip-overlay {
    opacity: 1;
}

.clip-card:hover .clip-image {
    transform: scale(1.1);
}

.play-icon {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    color: var(--color-dark);
    margin-bottom: 1rem;
    transition: var(--transition-smooth);
}

.clip-card:hover .play-icon {
    transform: scale(1.1);
}

.clip-title {
    color: var(--color-warm-white);
    font-size: 1.3rem;
    font-weight: 600;
    text-align: center;
}

.clip-link {
    padding: 1rem;
    background: var(--color-anthracite);
    text-align: center;
}

.clip-link a {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 600;
    font-size: 0.95rem;
    transition: var(--transition-smooth);
}

.clip-link a:hover {
    color: var(--color-yellow);
    transform: translateX(5px);
}

.clip-link i {
    font-size: 1.5rem;
}

.events-section {
    background: linear-gradient(
        180deg,
        var(--color-anthracite) 0%,
        var(--color-dark) 100%
    );
}

.events-list {
    margin-top: 4rem;
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

.event-card {
    background: rgba(26, 26, 26, 0.8);
    border: 1px solid rgba(212, 175, 55, 0.2);
    border-radius: 10px;
    padding: 2rem;
    display: flex;
    align-items: center;
    gap: 2rem;
    transition: var(--transition-smooth);
}

.event-card:hover {
    transform: translateX(10px);
    border-color: var(--color-gold);
    box-shadow: 0 10px 40px rgba(212, 175, 55, 0.3);
}

.event-date {
    min-width: 100px;
    text-align: center;
    padding: 1rem;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    border-radius: 10px;
    color: var(--color-dark);
}

.date-day {
    display: block;
    font-size: 2.5rem;
    font-weight: 900;
    line-height: 1;
}

.date-month {
    display: block;
    font-size: 1rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.1em;
}

.event-info {
    flex: 1;
}

.event-venue {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.5rem;
}

.event-time {
    color: var(--color-gold);
    font-size: 1.1rem;
}

.event-button {
    padding: 0.8rem 2rem;
    background: transparent;
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 700;
    text-transform: uppercase;
    border-radius: 50px;
    transition: var(--transition-smooth);
    white-space: nowrap;
}

.event-button:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
}

.gallery-section {
    background: var(--color-dark);
}

.gallery-grid {
    margin-top: 4rem;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 1.5rem;
}

.gallery-item {
    position: relative;
    height: 300px;
    overflow: hidden;
    border-radius: 10px;
    cursor: pointer;
}

.gallery-item img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: var(--transition-smooth);
}

.gallery-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
        to top,
        rgba(10, 10, 10, 0.95) 0%,
        rgba(10, 10, 10, 0.3) 100%
    );
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-end;
    padding: 1.5rem;
    opacity: 1;
    transition: var(--transition-smooth);
}

.gallery-item:hover .gallery-overlay {
    background: linear-gradient(
        to top,
        rgba(10, 10, 10, 0.95) 0%,
        rgba(10, 10, 10, 0.6) 100%
    );
}

.gallery-item:hover img {
    transform: scale(1.1);
}

.gallery-caption {
    color: var(--color-warm-white);
    font-size: 1rem;
    font-weight: 600;
    text-align: center;
    margin-bottom: 0.5rem;
    z-index: 2;
}

.zoom-icon {
    font-size: 2rem;
    color: var(--color-gold);
    opacity: 0;
    transition: var(--transition-smooth);
    margin-bottom: 1rem;
}

.gallery-item:hover .zoom-icon {
    opacity: 1;
}

.testimonials-section {
    padding: 8rem 0 12rem 0;
    background: var(--color-dark);
    overflow: hidden;
}

.testimonials-slider {
    position: relative;
    max-width: 900px;
    margin: 4rem auto 0;
    padding: 0 4rem;
}

.testimonials-track {
    display: flex;
    transition: transform 0.6s ease-in-out;
}

.testimonial-card {
    min-width: 100%;
    padding: 3rem;
    background: var(--color-anthracite);
    border-radius: 15px;
    border: 1px solid rgba(212, 175, 55, 0.2);
    text-align: center;
    box-sizing: border-box;
}

.testimonial-quote {
    font-size: 1.3rem;
    font-style: italic;
    color: var(--color-warm-white);
    line-height: 1.8;
    margin-bottom: 2rem;
    position: relative;
}

.testimonial-quote::before {
    content: '"';
    font-size: 4rem;
    color: var(--color-gold);
    position: absolute;
    top: -1.5rem;
    left: -1rem;
    opacity: 0.3;
}

.testimonial-author {
    font-weight: 700;
    color: var(--color-gold);
    font-size: 1.1rem;
    margin-bottom: 0.3rem;
}

.testimonial-role {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.95rem;
}

.testimonial-stars {
    color: var(--color-gold);
    font-size: 1.2rem;
    margin-bottom: 1.5rem;
}

.slider-nav {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background: rgba(212, 175, 55, 0.2);
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    width: 50px;
    height: 50px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: var(--transition-smooth);
    font-size: 1.5rem;
    z-index: 10;
}

.slider-nav:hover {
    background: var(--color-gold);
    color: var(--color-dark);
}

.slider-nav.prev {
    left: 0;
}

.slider-nav.next {
    right: 0;
}

.slider-dots {
    display: none;
    justify-content: center;
    gap: 0.8rem;
    margin-top: 2rem;
}

.slider-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: rgba(212, 175, 55, 0.3);
    cursor: pointer;
    transition: var(--transition-smooth);
}

.slider-dot.active {
    background: var(--color-gold);
    transform: scale(1.3);
}

.footer {
    margin-top: 6rem;
    background: var(--color-anthracite);
    padding: 4rem 0 2rem;
    border-top: 1px solid rgba(212, 175, 55, 0.2);
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 3rem;
    margin-bottom: 3rem;
}

.footer h3 {
    color: var(--color-gold);
    font-size: 1.5rem;
    margin-bottom: 1.5rem;
}

.social-links {
    display: flex;
    gap: 1rem;
}

.social-link {
    width: 50px;
    height: 50px;
    background: rgba(212, 175, 55, 0.1);
    border: 1px solid var(--color-gold);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--color-gold);
    transition: var(--transition-smooth);
}

.social-link i {
    font-size: 24px;
}

.social-link:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    transform: translateY(-5px);
}

.footer-contact a {
    color: var(--color-gold);
    text-decoration: none;
    transition: var(--transition-smooth);
}

.footer-contact a:hover {
    color: var(--color-yellow);
}

.footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid rgba(212, 175, 55, 0.1);
    color: rgba(245, 245, 220, 0.6);
}

.lightbox {
    display: none;
    position: fixed;
    z-index: 1000;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.95);
    align-items: center;
    justify-content: center;
}

.lightbox.active {
    display: flex;
}

.lightbox-image {
    max-width: 90%;
    max-height: 90%;
    object-fit: contain;
}

.lightbox-close {
    position: absolute;
    top: 2rem;
    right: 2rem;
    font-size: 3rem;
    color: var(--color-gold);
    cursor: pointer;
    transition: var(--transition-smooth);
}

.lightbox-close:hover {
    color: var(--color-yellow);
}

.lightbox-prev,
.lightbox-next {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    font-size: 3rem;
    color: var(--color-gold);
    cursor: pointer;
    padding: 1rem;
    transition: var(--transition-smooth);
    user-select: none;
}

.lightbox-prev {
    left: 2rem;
}

.lightbox-next {
    right: 2rem;
}

.lightbox-prev:hover,
.lightbox-next:hover {
    color: var(--color-yellow);
}

.fade-in {
    animation: fadeIn 1.5s ease-in;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.fade-in-scroll {
    opacity: 0;
    transform: translateY(50px);
    transition: opacity 0.8s ease-out, transform 0.8s ease-out;
}

.fade-in-scroll.visible {
    opacity: 1;
    transform: translateY(0);
}

.page-hero {
    position: relative;
    height: 50vh;
    min-height: 400px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(
        135deg,
        var(--color-dark) 0%,
        var(--color-anthracite) 100%
    );
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    border-bottom: 2px solid rgba(212, 175, 55, 0.2);
    overflow: hidden;
}

.page-hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
        135deg,
        rgba(10, 10, 10, 0.8) 0%,
        rgba(26, 26, 26, 0.7) 100%
    );
}

.page-hero-content {
    position: relative;
    z-index: 2;
    text-align: center;
}

.page-title {
    font-family: 'Playfair Display', serif;
    font-size: clamp(3rem, 7vw, 5rem);
    font-weight: 900;
    color: var(--color-gold);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    margin-bottom: 1rem;
}

.page-subtitle {
    font-size: clamp(1rem, 2vw, 1.3rem);
    font-weight: 300;
    color: var(--color-warm-white);
    letter-spacing: 0.15em;
}

.about-bio {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.about-bio-grid {
    display: grid;
    grid-template-columns: 1fr 1.5fr;
    gap: 4rem;
    align-items: start;
}

.about-bio-image {
    position: sticky;
    top: 100px;
}

.about-bio-image img {
    width: 100%;
    border-radius: 10px;
    box-shadow: 0 20px 60px rgba(212, 175, 55, 0.2);
}

.qualities-section {
    padding: 6rem 0;
    background: var(--color-dark);
}

.qualities-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    margin-top: 4rem;
}

.quality-card {
    background: var(--color-anthracite);
    border: 1px solid rgba(212, 175, 55, 0.1);
    border-radius: 10px;
    padding: 2rem;
    text-align: center;
    transition: var(--transition-smooth);
}

.quality-card:hover {
    transform: translateY(-10px);
    border-color: var(--color-gold);
    box-shadow: 0 10px 40px rgba(212, 175, 55, 0.2);
}

.quality-icon {
    font-size: 3rem;
    margin-bottom: 1rem;
}

.quality-card h3 {
    color: var(--color-gold);
    font-size: 1.3rem;
    margin-bottom: 1rem;
}

.blog-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.blog-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 2rem;
    margin-top: 4rem;
}

.blog-card {
    background: var(--color-dark);
    border-radius: 10px;
    overflow: hidden;
    transition: var(--transition-smooth);
}

.blog-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 40px rgba(212, 175, 55, 0.3);
}

.blog-image {
    height: 250px;
    background-size: cover;
    background-position: center;
}

.blog-content {
    padding: 2rem;
}

.blog-date {
    color: var(--color-gold);
    font-size: 0.9rem;
    font-weight: 600;
    letter-spacing: 0.1em;
}

.blog-title {
    color: var(--color-warm-white);
    font-size: 1.4rem;
    font-weight: 700;
    margin: 1rem 0;
    line-height: 1.4;
}

.blog-excerpt {
    color: rgba(245, 245, 220, 0.8);
    line-height: 1.8;
    margin-bottom: 1rem;
}

.blog-link {
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 600;
    transition: var(--transition-smooth);
}

.blog-link:hover {
    color: var(--color-yellow);
}

.discography-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.albums-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 3rem;
    margin-top: 4rem;
}

.album-card {
    background: var(--color-dark);
    border-radius: 10px;
    overflow: hidden;
    transition: var(--transition-smooth);
}

.album-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 50px rgba(212, 175, 55, 0.3);
}

.album-cover {
    position: relative;
    aspect-ratio: 1;
    overflow: hidden;
}

.album-cover img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.album-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(10, 10, 10, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: var(--transition-smooth);
}

.album-card:hover .album-overlay {
    opacity: 1;
}

.play-button-large {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    border: none;
    color: var(--color-dark);
    font-size: 2rem;
    cursor: pointer;
    transition: var(--transition-smooth);
    display: flex;
    text-decoration: none;
    text-align: center;
    justify-content: center;
    align-items: center;
}

.play-button-large:hover {
    transform: scale(1.1);
}

.album-info {
    padding: 1.5rem;
}

.album-title {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.5rem;
}

.album-meta {
    color: var(--color-gold);
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
}

.album-details {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.9rem;
}

.player-section {
    padding: 6rem 0;
    background: var(--color-dark);
}

.music-player {
    background: var(--color-anthracite);
    border-radius: 15px;
    overflow: hidden;
    margin-top: 4rem;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
}

.player-header {
    padding: 2rem;
    background: linear-gradient(
        135deg,
        rgba(212, 175, 55, 0.1),
        rgba(244, 208, 63, 0.05)
    );
    border-bottom: 1px solid rgba(212, 175, 55, 0.2);
}

.now-playing-info {
    display: flex;
    align-items: center;
    gap: 1.5rem;
}

.now-playing-cover {
    width: 80px;
    height: 80px;
    border-radius: 8px;
    overflow: hidden;
}

.now-playing-cover img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.track-name {
    font-size: 1.3rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.3rem;
}

.artist-name {
    color: var(--color-gold);
    font-size: 1rem;
}

.playlist {
    max-height: 500px;
    overflow-y: auto;
}

.track-item {
    display: grid;
    grid-template-columns: 40px 40px 1fr auto;
    gap: 1rem;
    align-items: center;
    padding: 1rem 2rem;
    border-bottom: 1px solid rgba(212, 175, 55, 0.1);
    cursor: pointer;
    transition: var(--transition-smooth);
}

.track-item:hover {
    background: rgba(212, 175, 55, 0.1);
}

.track-number {
    color: rgba(245, 245, 220, 0.5);
    font-size: 0.9rem;
    text-align: center;
}

.track-play-btn {
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background: var(--color-gold);
    border: none;
    color: var(--color-dark);
    font-size: 0.8rem;
    cursor: pointer;
    opacity: 0;
    transition: var(--transition-smooth);
}

.track-item:hover .track-play-btn {
    opacity: 1;
}

.track-info-wrapper {
    display: flex;
    flex-direction: column;
}

.track-title-name {
    color: var(--color-warm-white);
    font-weight: 600;
    margin-bottom: 0.3rem;
}

.track-album-name {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.9rem;
}

.track-duration {
    color: rgba(245, 245, 220, 0.5);
    font-size: 0.9rem;
}

.streaming-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.streaming-platforms {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    gap: 2rem;
    margin-top: 4rem;
}

.platform-link {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
    padding: 2rem;
    background: var(--color-dark);
    border: 2px solid rgba(212, 175, 55, 0.2);
    border-radius: 15px;
    color: var(--color-gold);
    text-decoration: none;
    transition: var(--transition-smooth);
    min-width: 150px;
}

.platform-link:hover {
    border-color: var(--color-gold);
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(212, 175, 55, 0.3);
}

.platform-link span {
    font-weight: 600;
}

.contact-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.contact-grid {
    display: grid;
    grid-template-columns: 1fr 1.2fr;
    gap: 4rem;
    margin-top: 3rem;
}

.contact-info {
    background: var(--color-dark);
    padding: 3rem;
    border-radius: 15px;
}

.contact-intro {
    font-size: 1.1rem;
    line-height: 1.8;
    margin-bottom: 2rem;
    color: rgba(245, 245, 220, 0.9);
}

.contact-methods {
    margin-bottom: 3rem;
}

.contact-method {
    display: flex;
    align-items: start;
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.method-icon {
    font-size: 2rem;
    min-width: 50px;
}

.method-info h3 {
    color: var(--color-gold);
    font-size: 1.2rem;
    margin-bottom: 0.5rem;
}

.method-info a {
    color: var(--color-warm-white);
    text-decoration: none;
    transition: var(--transition-smooth);
}

.method-info a:hover {
    color: var(--color-gold);
}

.contact-social h3 {
    color: var(--color-gold);
    margin-bottom: 1rem;
}

.contact-form-container {
    background: var(--color-dark);
    padding: 3rem;
    border-radius: 15px;
}

.contact-form {
    margin-top: 2rem;
}

.form-group {
    margin-bottom: 1.5rem;
}

.form-group label {
    display: block;
    color: var(--color-gold);
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 1rem;
    background: var(--color-anthracite);
    border: 1px solid rgba(212, 175, 55, 0.2);
    border-radius: 8px;
    color: var(--color-warm-white);
    font-family: 'Montserrat', sans-serif;
    font-size: 1rem;
    transition: var(--transition-smooth);
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--color-gold);
    box-shadow: 0 0 0 3px rgba(212, 175, 55, 0.1);
}

.submit-button {
    width: 100%;
    padding: 1.2rem 3rem;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    border: none;
    border-radius: 50px;
    font-weight: 700;
    font-size: 1.1rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.submit-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 30px rgba(212, 175, 55, 0.6);
}

.alert {
    padding: 1rem;
    border-radius: 8px;
    margin-bottom: 2rem;
}

.alert-success {
    background: rgba(76, 175, 80, 0.2);
    border: 1px solid rgba(76, 175, 80, 0.5);
    color: #4caf50;
}

.alert-error {
    background: rgba(244, 67, 54, 0.2);
    border: 1px solid rgba(244, 67, 54, 0.5);
    color: #f44336;
}

.footer-section {
    flex: 1;
    min-width: 250px;
}

.footer-links {
    list-style: none;
    padding: 0;
}

.footer-links li {
    margin-bottom: 0.8rem;
}

.footer-links a {
    color: var(--color-warm-white);
    text-decoration: none;
    transition: var(--transition-smooth);
}

.footer-links a:hover {
    color: var(--color-gold);
}

@media (max-width: 768px) {
    .container {
        padding: 0 1.5rem;
    }

    .mobile-menu-toggle {
        display: flex;
    }

    .mobile-menu-toggle.active span:nth-child(1) {
        transform: rotate(45deg) translate(5px, 5px);
    }

    .mobile-menu-toggle.active span:nth-child(2) {
        opacity: 0;
    }

    .mobile-menu-toggle.active span:nth-child(3) {
        transform: rotate(-45deg) translate(7px, -6px);
    }

    .nav-menu {
        position: fixed;
        top: 80px;
        left: -100%;
        width: 100%;
        height: calc(100vh - 80px);
        background: rgba(10, 10, 10, 0.98);
        flex-direction: column;
        padding: 2rem;
        transition: var(--transition-smooth);
        z-index: 999;
    }

    .nav-menu.active {
        left: 0;
    }

    .nav-link {
        font-size: 1.1rem;
        padding: 1rem 0;
    }

    .hero-title {
        font-size: 2rem;
    }

    .hero-subtitle {
        font-size: 0.85rem;
        letter-spacing: 0.15em;
    }

    .section-title {
        font-size: 1.8rem;
    }

    .page-title {
        font-size: 2rem;
    }

    .cta-button {
        padding: 0.7rem 1.5rem;
        font-size: 0.85rem;
    }

    .cta-button-secondary {
        padding: 0.6rem 1.2rem;
        font-size: 0.8rem;
    }

    .bio-text {
        font-size: 0.95rem;
    }

    .highlight-text {
        font-size: 1.1rem;
    }

    .bio-grid {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .about-bio-grid {
        grid-template-columns: 1fr;
    }

    .about-bio-image {
        position: relative;
        top: 0;
    }

    .qualities-grid {
        grid-template-columns: 1fr;
    }

    .blog-grid {
        grid-template-columns: 1fr;
    }

    .albums-grid {
        grid-template-columns: 1fr;
    }

    .contact-grid {
        grid-template-columns: 1fr;
    }

    .streaming-platforms {
        flex-direction: column;
        align-items: center;
    }

    .track-item {
        grid-template-columns: 40px 1fr auto;
        padding: 1rem;
    }

    .track-play {
        display: none;
    }

    .clips-grid {
        grid-template-columns: 1fr;
    }

    .clip-title {
        font-size: 1.1rem;
    }

    .event-card {
        flex-direction: column;
        text-align: center;
        padding: 1.5rem;
    }

    .event-venue {
        font-size: 1.1rem;
    }

    .event-time {
        font-size: 0.9rem;
    }

    .event-button {
        padding: 0.6rem 1.2rem;
        font-size: 0.8rem;
    }

    .date-day {
        font-size: 1.8rem;
    }

    .date-month {
        font-size: 0.8rem;
    }

    .gallery-grid {
        grid-template-columns: 1fr;
    }

    .footer-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .social-links {
        justify-content: center;
    }

    .footer h3 {
