}

.latest-clip-card {
    background: rgba(26, 26, 26, 0.6);
    border: 1px solid rgba(212, 175, 55, 0.1);
    border-radius: 15px;
    overflow: hidden;
    transition: var(--transition-smooth);
    cursor: pointer;
}

.latest-clip-card:hover {
    transform: translateY(-10px);
    border-color: var(--color-gold);
