}

.latest-clip-thumbnail {
    position: relative;
    width: 100%;
    height: 240px;
    overflow: hidden;
}

.latest-clip-thumbnail img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: var(--transition-smooth);
}

.latest-clip-card:hover .latest-clip-thumbnail img {
    transform: scale(1.1);
}

.clip-badge {
    position: absolute;
    top: 1rem;
    right: 1rem;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    padding: 0.4rem 1rem;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    z-index: 2;
}

.latest-clip-info {
    padding: 1.5rem;
    text-align: center;
}

.latest-clip-title {
    font-size: 1.4rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.5rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.latest-clip-views {
    color: var(--color-gold);
    font-size: 0.95rem;
    margin-bottom: 1rem;
    font-weight: 600;
}

.watch-button {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.7rem 1.8rem;
    background: transparent;
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 0.9rem;
    border-radius: 50px;
    transition: var(--transition-smooth);
    letter-spacing: 0.05em;
}

.watch-button:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    transform: scale(1.05);
}

.watch-button i {
    font-size: 1.3rem;
}

/* Carousel Gallery Section Styles */
.carousel-gallery-section {
    padding: 8rem 0;
    background: var(--color-dark);
    overflow: hidden;
}

.carousel-container {
    margin-top: 4rem;
    overflow: hidden;
    position: relative;
    width: 100%;
}

.carousel-container::before,
.carousel-container::after {
    content: '';
    position: absolute;
    top: 0;
    width: 150px;
    height: 100%;
    z-index: 2;
    pointer-events: none;
}

.carousel-container::before {
    left: 0;
    background: linear-gradient(to right, var(--color-dark) 0%, transparent 100%);
}

.carousel-container::after {
    right: 0;
    background: linear-gradient(to left, var(--color-dark) 0%, transparent 100%);
}

.carousel-track {
    display: flex;
    width: fit-content;
    animation: scroll-carousel 120s linear infinite;
}

.carousel-track:hover {
    animation-play-state: paused;
}

.carousel-slide {
    flex-shrink: 0;
    width: 350px;
    height: 250px;
    margin: 0 1rem;
    border-radius: 10px;
    overflow: hidden;
    position: relative;
    transition: var(--transition-smooth);
}

.carousel-slide:hover {
    transform: scale(1.05);
    box-shadow: 0 10px 30px rgba(212, 175, 55, 0.4);
    z-index: 3;
}

.carousel-slide img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
}

@keyframes scroll-carousel {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(-50%);
    }
}

/* Mobile responsive for carousel */
@media (max-width: 768px) {
    .carousel-slide {
        width: 280px;
        height: 200px;
    }

    .carousel-container::before,
    .carousel-container::after {
        width: 80px;
    }
}

