/* Trophy Modal Styles */
.trophy-modal {
    display: none;
    position: fixed;
    z-index: 2000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.95);
    overflow-y: auto;
    animation: fadeIn 0.3s ease;
}

.trophy-modal.active {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 2rem;
}

.trophy-modal-content {
    background: var(--color-anthracite);
    border: 2px solid var(--color-gold);
    border-radius: 15px;
    max-width: 900px;
    width: 100%;
    max-height: 90vh;
    overflow-y: auto;
    position: relative;
    animation: slideIn 0.3s ease;
}

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
}

@keyframes slideIn {
    from {
        transform: translateY(-50px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.trophy-modal-close {
    position: absolute;
    top: 1.5rem;
    right: 2rem;
    font-size: 3rem;
    color: var(--color-gold);
    cursor: pointer;
    z-index: 10;
    transition: var(--transition-smooth);
}

.trophy-modal-close:hover {
    color: var(--color-yellow);
    transform: rotate(90deg);
}

.trophy-modal-header {
    padding: 3rem 2rem 2rem;
    text-align: center;
    border-bottom: 1px solid rgba(212, 175, 55, 0.2);
}

