}

.trophy-category-card p {
    font-size: 0.95rem;
    color: rgba(245, 245, 220, 0.8);
    line-height: 1.6;
}

/* Mobile responsiveness */
@media (max-width: 768px) {
    .trophies-grid {
        grid-template-columns: 1fr;
