.track-artist-spotify {
    font-size: 0.9rem;
    color: rgba(245, 245, 220, 0.6);
}

.playlist-track:hover .track-name-spotify {
    color: var(--color-gold);
}

.track-duration-spotify {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.95rem;
    text-align: right;
}

/* Mobile responsive */
@media (max-width: 768px) {
    .album-playlist-header {
        flex-direction: column;
        align-items: center;
        text-align: center;
    }

    .album-playlist-title {
        font-size: 2.5rem;
    }

    .playlist-header-row {
        grid-template-columns: 40px 1fr 80px;
    }

    .playlist-track {
        grid-template-columns: 40px 1fr 80px;
    }

    .track-play-btn-spotify {
        width: 35px;
        height: 35px;
        font-size: 1.2rem;
    }
}

/* Fix carousel infinite loop - override previous animation */
.carousel-track {
    animation: scroll-carousel-fixed 120s linear infinite;
}

@keyframes scroll-carousel-fixed {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(calc(-350px * 25 - 25rem));
    }
}

.footer-credit {
    text-align: center;
    padding-top: 1.5rem;
    margin-top: 1.5rem;
    border-top: 1px solid rgba(212, 175, 55, 0.08);
}

.footer-credit p {
    font-size: 13px;
    color: #aaa;
    line-height: 1.6;
}

.footer-credit strong {
    color: #c0c0c0;
    font-weight: 600;
}

.video-modal {
    display: none;
    position: fixed;
    z-index: 2000;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.video-modal.active {
    display: flex;
    opacity: 1;
}

.video-modal-content {
    position: relative;
    width: 90%;
    max-width: 1200px;
    background: var(--color-anthracite);
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 10px 50px rgba(212, 175, 55, 0.3);
    animation: modalSlideIn 0.4s ease-out;
}

@keyframes modalSlideIn {
    from {
        transform: translateY(-50px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.video-modal-close {
    position: absolute;
    top: -45px;
    right: 0;
    font-size: 2.5rem;
    color: var(--color-gold);
    cursor: pointer;
    transition: var(--transition-smooth);
    z-index: 2001;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(26, 26, 26, 0.8);
    border-radius: 50%;
}

.video-modal-close:hover {
    color: var(--color-yellow);
    transform: rotate(90deg);
}

.video-wrapper {
    position: relative;
    width: 100%;
    padding-bottom: 56.25%;
    height: 0;
}

.video-wrapper iframe {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

.latest-clip-thumbnail {
    cursor: pointer;
    position: relative;
}

.latest-clip-thumbnail::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0);
    transition: var(--transition-smooth);
}

.latest-clip-thumbnail:hover::after {
    background: rgba(0, 0, 0, 0.2);
}

@media (max-width: 768px) {
    .video-modal-content {
        width: 95%;
    }

    .video-modal-close {
        top: 10px;
        right: 10px;
        background: rgba(26, 26, 26, 0.95);
    }

    .footer-credit p {
        font-size: 12px;
        padding: 0 1rem;
    }
}

/* Cacher les points de pagination des témoignages */
.testimonials-section .slider-dots {
    display: none !important;
}

/* Ajouter de l'espace entre les témoignages et le footer */
#temoignages {
    margin-bottom: 4rem;
}
