css/style.css.content:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --color-dark: #0a0a0a;
    --color-anthracite: #1a1a1a;
    --color-gold: #d4af37;
    --color-yellow: #f4d03f;
    --color-warm-white: #f5f5dc;
    --color-overlay: rgba(10, 10, 10, 0.7);
    --transition-smooth: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Montserrat', sans-serif;
    background-color: var(--color-dark);
    color: var(--color-warm-white);
    line-height: 1.6;
    overflow-x: hidden;
    padding-top: 80px;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
}

.container-full {
    width: 100%;
    padding: 0 2rem;
}

.hero-section {
    position: relative;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}

.hero-image {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
}

.parallax-bg {
    will-change: transform;
}

.hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(10, 10, 10, 0.8) 0%, rgba(26, 26, 26, 0.6) 100%);
}

.hero-content {
    position: relative;
    z-index: 2;
    text-align: center;
    padding: 2rem;
}

.hero-title {
    font-family: 'Playfair Display', serif;
    font-size: clamp(3rem, 10vw, 8rem);
    font-weight: 900;
    color: var(--color-gold);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    margin-bottom: 1rem;
    text-shadow: 0 4px 20px rgba(212, 175, 55, 0.5);
}

.hero-subtitle {
    font-size: clamp(1.2rem, 3vw, 2rem);
    font-weight: 300;
    color: var(--color-warm-white);
    letter-spacing: 0.3em;
    margin-bottom: 3rem;
    text-transform: uppercase;
}

.cta-button {
    display: inline-block;
    padding: 1rem 3rem;
    background: linear-gradient(135deg, var(--color-gold) 0%, var(--color-yellow) 100%);
    color: var(--color-dark);
    text-decoration: none;
    font-weight: 700;
    font-size: 1.1rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    border-radius: 50px;
    transition: var(--transition-smooth);
    box-shadow: 0 4px 20px rgba(212, 175, 55, 0.4);
}

.cta-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 30px rgba(212, 175, 55, 0.6);
}

.hero-buttons {
    display: flex;
    gap: 1.5rem;
    justify-content: center;
    flex-wrap: wrap;
}

.cta-button-secondary {
    display: inline-block;
    padding: 0.8rem 2rem;
    background: transparent;
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    border-radius: 50px;
    transition: var(--transition-smooth);
    margin-top: 1rem;
}

.cta-button-secondary:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
}

.text-center {
    text-align: center;
}

.main-header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background: rgba(10, 10, 10, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid rgba(212, 175, 55, 0.1);
    z-index: 1000;
    transition: var(--transition-smooth);
}

.main-nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 0;
}

.logo {
    text-decoration: none;
}

.logo-text {
    font-family: 'Playfair Display', serif;
    font-size: 1.8rem;
    font-weight: 900;
    color: var(--color-gold);
    letter-spacing: 0.05em;
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: 2rem;
    margin: 0;
}

.nav-link {
    color: var(--color-warm-white);
    text-decoration: none;
    font-weight: 600;
    text-transform: uppercase;
    font-size: 0.9rem;
    letter-spacing: 0.05em;
    transition: var(--transition-smooth);
    position: relative;
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, var(--color-gold), var(--color-yellow));
    transition: var(--transition-smooth);
}

.nav-link:hover,
.nav-link.active {
    color: var(--color-gold);
}

.nav-link:hover::after,
.nav-link.active::after {
    width: 100%;
}

.mobile-menu-toggle {
    display: none;
    flex-direction: column;
    gap: 4px;
    background: none;
    border: none;
    cursor: pointer;
    padding: 0.5rem;
}

.mobile-menu-toggle span {
    width: 25px;
    height: 3px;
    background: var(--color-gold);
    border-radius: 2px;
    transition: var(--transition-smooth);
}

.scroll-indicator {
    position: absolute;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
    z-index: 2;
}

.scroll-indicator span {
    display: block;
    width: 30px;
    height: 50px;
    border: 2px solid var(--color-gold);
    border-radius: 25px;
    position: relative;
}

.scroll-indicator span::before {
    content: '';
    position: absolute;
    top: 10px;
    left: 50%;
    width: 6px;
    height: 6px;
    background: var(--color-gold);
    border-radius: 50%;
    transform: translateX(-50%);
    animation: scroll-down 2s infinite;
}

@keyframes scroll-down {
    0%, 20% {
        transform: translateX(-50%) translateY(0);
        opacity: 1;
    }
    100% {
        transform: translateX(-50%) translateY(20px);
        opacity: 0;
    }
}

.bio-section, .clips-section, .events-section, .gallery-section {
    padding: 8rem 0;
    position: relative;
}

.bio-section {
    background: linear-gradient(180deg, var(--color-dark) 0%, var(--color-anthracite) 100%);
}

.bio-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: center;
}

.bio-image {
    position: relative;
    overflow: hidden;
    border-radius: 10px;
    box-shadow: 0 20px 60px rgba(212, 175, 55, 0.2);
}

.bio-image img {
    width: 100%;
    height: auto;
    display: block;
    transition: var(--transition-smooth);
}

.bio-image:hover img {
    transform: scale(1.05);
}

.section-title {
    font-family: 'Playfair Display', serif;
    font-size: clamp(2.5rem, 5vw, 4rem);
    font-weight: 900;
    color: var(--color-gold);
    margin-bottom: 2rem;
    position: relative;
    display: inline-block;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 0;
    width: 60px;
    height: 4px;
    background: linear-gradient(90deg, var(--color-gold), var(--color-yellow));
}

.section-title.centered {
    display: block;
    text-align: center;
}

.section-title.centered::after {
    left: 50%;
    transform: translateX(-50%);
}

.bio-text {
    font-size: 1.1rem;
    line-height: 1.8;
}

.highlight-text {
    font-size: 1.4rem;
    font-style: italic;
    color: var(--color-gold);
    margin-bottom: 1.5rem;
    line-height: 1.6;
}

.clips-section {
    background: var(--color-anthracite);
}

.clips-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
    margin-top: 4rem;
}

.clip-card {
    position: relative;
    height: 350px;
    border-radius: 10px;
    overflow: hidden;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.clip-image {
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    transition: var(--transition-smooth);
}

.clip-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(10, 10, 10, 0.7);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: var(--transition-smooth);
}

.clip-card:hover .clip-overlay {
    opacity: 1;
}

.clip-card:hover .clip-image {
    transform: scale(1.1);
}

.play-icon {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    color: var(--color-dark);
    margin-bottom: 1rem;
    transition: var(--transition-smooth);
}

.clip-card:hover .play-icon {
    transform: scale(1.1);
}

.clip-title {
    color: var(--color-warm-white);
    font-size: 1.3rem;
    font-weight: 600;
    text-align: center;
}

.events-section {
    background: linear-gradient(180deg, var(--color-anthracite) 0%, var(--color-dark) 100%);
}

.events-list {
    margin-top: 4rem;
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

.event-card {
    background: rgba(26, 26, 26, 0.8);
    border: 1px solid rgba(212, 175, 55, 0.2);
    border-radius: 10px;
    padding: 2rem;
    display: flex;
    align-items: center;
    gap: 2rem;
    transition: var(--transition-smooth);
}

.event-card:hover {
    transform: translateX(10px);
    border-color: var(--color-gold);
    box-shadow: 0 10px 40px rgba(212, 175, 55, 0.3);
}

.event-date {
    min-width: 100px;
    text-align: center;
    padding: 1rem;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    border-radius: 10px;
    color: var(--color-dark);
}

.date-day {
    display: block;
    font-size: 2.5rem;
    font-weight: 900;
    line-height: 1;
}

.date-month {
    display: block;
    font-size: 1rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.1em;
}

.event-info {
    flex: 1;
}

.event-venue {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.5rem;
}

.event-time {
    color: var(--color-gold);
    font-size: 1.1rem;
}

.event-button {
    padding: 0.8rem 2rem;
    background: transparent;
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 700;
    text-transform: uppercase;
    border-radius: 50px;
    transition: var(--transition-smooth);
    white-space: nowrap;
}

.event-button:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
}

.gallery-section {
    background: var(--color-dark);
}

.gallery-grid {
    margin-top: 4rem;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 1.5rem;
}

.gallery-item {
    position: relative;
    height: 300px;
    overflow: hidden;
    border-radius: 10px;
    cursor: pointer;
}

.gallery-item img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: var(--transition-smooth);
}

.gallery-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to top, rgba(10, 10, 10, 0.95) 0%, rgba(10, 10, 10, 0.3) 100%);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-end;
    padding: 1.5rem;
    opacity: 1;
    transition: var(--transition-smooth);
}

.gallery-item:hover .gallery-overlay {
    background: linear-gradient(to top, rgba(10, 10, 10, 0.95) 0%, rgba(10, 10, 10, 0.6) 100%);
}

.gallery-item:hover img {
    transform: scale(1.1);
}

.gallery-caption {
    color: var(--color-warm-white);
    font-size: 1rem;
    font-weight: 600;
    text-align: center;
    margin-bottom: 0.5rem;
    z-index: 2;
}

.zoom-icon {
    font-size: 2rem;
    color: var(--color-gold);
    opacity: 0;
    transition: var(--transition-smooth);
    margin-bottom: 1rem;
}

.gallery-item:hover .zoom-icon {
    opacity: 1;
}

.testimonials-section {
    padding: 8rem 0;
    background: var(--color-dark);
    overflow: hidden;
}

.testimonials-slider {
    position: relative;
    max-width: 900px;
    margin: 4rem auto 0;
    padding: 0 4rem;
}

.testimonials-track {
    display: flex;
    transition: transform 0.6s ease-in-out;
}

.testimonial-card {
    min-width: 100%;
    padding: 3rem;
    background: var(--color-anthracite);
    border-radius: 15px;
    border: 1px solid rgba(212, 175, 55, 0.2);
    text-align: center;
    box-sizing: border-box;
}

.testimonial-quote {
    font-size: 1.3rem;
    font-style: italic;
    color: var(--color-warm-white);
    line-height: 1.8;
    margin-bottom: 2rem;
    position: relative;
}

.testimonial-quote::before {
    content: '"';
    font-size: 4rem;
    color: var(--color-gold);
    position: absolute;
    top: -1.5rem;
    left: -1rem;
    opacity: 0.3;
}

.testimonial-author {
    font-weight: 700;
    color: var(--color-gold);
    font-size: 1.1rem;
    margin-bottom: 0.3rem;
}

.testimonial-role {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.95rem;
}

.testimonial-stars {
    color: var(--color-gold);
    font-size: 1.2rem;
    margin-bottom: 1.5rem;
}

.slider-nav {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background: rgba(212, 175, 55, 0.2);
    border: 2px solid var(--color-gold);
    color: var(--color-gold);
    width: 50px;
    height: 50px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: var(--transition-smooth);
    font-size: 1.5rem;
    z-index: 10;
}

.slider-nav:hover {
    background: var(--color-gold);
    color: var(--color-dark);
}

.slider-nav.prev {
    left: 0;
}

.slider-nav.next {
    right: 0;
}

.slider-dots {
    display: flex;
    justify-content: center;
    gap: 0.8rem;
    margin-top: 2rem;
}

.slider-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: rgba(212, 175, 55, 0.3);
    cursor: pointer;
    transition: var(--transition-smooth);
}

.slider-dot.active {
    background: var(--color-gold);
    transform: scale(1.3);
}

.footer {
    background: var(--color-anthracite);
    padding: 4rem 0 2rem;
    border-top: 1px solid rgba(212, 175, 55, 0.2);
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 3rem;
    margin-bottom: 3rem;
}

.footer h3 {
    color: var(--color-gold);
    font-size: 1.5rem;
    margin-bottom: 1.5rem;
}

.social-links {
    display: flex;
    gap: 1rem;
}

.social-link {
    width: 50px;
    height: 50px;
    background: rgba(212, 175, 55, 0.1);
    border: 1px solid var(--color-gold);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--color-gold);
    transition: var(--transition-smooth);
}

.social-link i {
    font-size: 24px;
}

.social-link:hover {
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    transform: translateY(-5px);
}

.footer-contact a {
    color: var(--color-gold);
    text-decoration: none;
    transition: var(--transition-smooth);
}

.footer-contact a:hover {
    color: var(--color-yellow);
}

.footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid rgba(212, 175, 55, 0.1);
    color: rgba(245, 245, 220, 0.6);
}

.lightbox {
    display: none;
    position: fixed;
    z-index: 1000;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.95);
    align-items: center;
    justify-content: center;
}

.lightbox.active {
    display: flex;
}

.lightbox-image {
    max-width: 90%;
    max-height: 90%;
    object-fit: contain;
}

.lightbox-close {
    position: absolute;
    top: 2rem;
    right: 2rem;
    font-size: 3rem;
    color: var(--color-gold);
    cursor: pointer;
    transition: var(--transition-smooth);
}

.lightbox-close:hover {
    color: var(--color-yellow);
}

.lightbox-prev,
.lightbox-next {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    font-size: 3rem;
    color: var(--color-gold);
    cursor: pointer;
    padding: 1rem;
    transition: var(--transition-smooth);
    user-select: none;
}

.lightbox-prev {
    left: 2rem;
}

.lightbox-next {
    right: 2rem;
}

.lightbox-prev:hover,
.lightbox-next:hover {
    color: var(--color-yellow);
}

.fade-in {
    animation: fadeIn 1.5s ease-in;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.fade-in-scroll {
    opacity: 0;
    transform: translateY(50px);
    transition: opacity 0.8s ease-out, transform 0.8s ease-out;
}

.fade-in-scroll.visible {
    opacity: 1;
    transform: translateY(0);
}

.page-hero {
    position: relative;
    height: 50vh;
    min-height: 400px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, var(--color-dark) 0%, var(--color-anthracite) 100%);
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    border-bottom: 2px solid rgba(212, 175, 55, 0.2);
    overflow: hidden;
}

.page-hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(10, 10, 10, 0.8) 0%, rgba(26, 26, 26, 0.7) 100%);
}

.page-hero-content {
    position: relative;
    z-index: 2;
    text-align: center;
}

.page-title {
    font-family: 'Playfair Display', serif;
    font-size: clamp(3rem, 7vw, 5rem);
    font-weight: 900;
    color: var(--color-gold);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    margin-bottom: 1rem;
}

.page-subtitle {
    font-size: clamp(1rem, 2vw, 1.3rem);
    font-weight: 300;
    color: var(--color-warm-white);
    letter-spacing: 0.15em;
}

.about-bio {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.about-bio-grid {
    display: grid;
    grid-template-columns: 1fr 1.5fr;
    gap: 4rem;
    align-items: start;
}

.about-bio-image {
    position: sticky;
    top: 100px;
}

.about-bio-image img {
    width: 100%;
    border-radius: 10px;
    box-shadow: 0 20px 60px rgba(212, 175, 55, 0.2);
}

.qualities-section {
    padding: 6rem 0;
    background: var(--color-dark);
}

.qualities-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    margin-top: 4rem;
}

.quality-card {
    background: var(--color-anthracite);
    border: 1px solid rgba(212, 175, 55, 0.1);
    border-radius: 10px;
    padding: 2rem;
    text-align: center;
    transition: var(--transition-smooth);
}

.quality-card:hover {
    transform: translateY(-10px);
    border-color: var(--color-gold);
    box-shadow: 0 10px 40px rgba(212, 175, 55, 0.2);
}

.quality-icon {
    font-size: 3rem;
    margin-bottom: 1rem;
}

.quality-card h3 {
    color: var(--color-gold);
    font-size: 1.3rem;
    margin-bottom: 1rem;
}

.blog-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.blog-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 2rem;
    margin-top: 4rem;
}

.blog-card {
    background: var(--color-dark);
    border-radius: 10px;
    overflow: hidden;
    transition: var(--transition-smooth);
}

.blog-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 40px rgba(212, 175, 55, 0.3);
}

.blog-image {
    height: 250px;
    background-size: cover;
    background-position: center;
}

.blog-content {
    padding: 2rem;
}

.blog-date {
    color: var(--color-gold);
    font-size: 0.9rem;
    font-weight: 600;
    letter-spacing: 0.1em;
}

.blog-title {
    color: var(--color-warm-white);
    font-size: 1.4rem;
    font-weight: 700;
    margin: 1rem 0;
    line-height: 1.4;
}

.blog-excerpt {
    color: rgba(245, 245, 220, 0.8);
    line-height: 1.8;
    margin-bottom: 1rem;
}

.blog-link {
    color: var(--color-gold);
    text-decoration: none;
    font-weight: 600;
    transition: var(--transition-smooth);
}

.blog-link:hover {
    color: var(--color-yellow);
}

.discography-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.albums-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 3rem;
    margin-top: 4rem;
}

.album-card {
    background: var(--color-dark);
    border-radius: 10px;
    overflow: hidden;
    transition: var(--transition-smooth);
}

.album-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 50px rgba(212, 175, 55, 0.3);
}

.album-cover {
    position: relative;
    aspect-ratio: 1;
    overflow: hidden;
}

.album-cover img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.album-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(10, 10, 10, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: var(--transition-smooth);
}

.album-card:hover .album-overlay {
    opacity: 1;
}

.play-button-large {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    border: none;
    color: var(--color-dark);
    font-size: 2rem;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.play-button-large:hover {
    transform: scale(1.1);
}

.album-info {
    padding: 1.5rem;
}

.album-title {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.5rem;
}

.album-meta {
    color: var(--color-gold);
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
}

.album-details {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.9rem;
}

.player-section {
    padding: 6rem 0;
    background: var(--color-dark);
}

.music-player {
    background: var(--color-anthracite);
    border-radius: 15px;
    overflow: hidden;
    margin-top: 4rem;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
}

.player-header {
    padding: 2rem;
    background: linear-gradient(135deg, rgba(212, 175, 55, 0.1), rgba(244, 208, 63, 0.05));
    border-bottom: 1px solid rgba(212, 175, 55, 0.2);
}

.now-playing-info {
    display: flex;
    align-items: center;
    gap: 1.5rem;
}

.now-playing-cover {
    width: 80px;
    height: 80px;
    border-radius: 8px;
    overflow: hidden;
}

.now-playing-cover img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.track-name {
    font-size: 1.3rem;
    font-weight: 700;
    color: var(--color-warm-white);
    margin-bottom: 0.3rem;
}

.artist-name {
    color: var(--color-gold);
    font-size: 1rem;
}

.playlist {
    max-height: 500px;
    overflow-y: auto;
}

.track-item {
    display: grid;
    grid-template-columns: 40px 40px 1fr auto;
    gap: 1rem;
    align-items: center;
    padding: 1rem 2rem;
    border-bottom: 1px solid rgba(212, 175, 55, 0.1);
    cursor: pointer;
    transition: var(--transition-smooth);
}

.track-item:hover {
    background: rgba(212, 175, 55, 0.1);
}

.track-number {
    color: rgba(245, 245, 220, 0.5);
    font-size: 0.9rem;
    text-align: center;
}

.track-play-btn {
    width: 30px;
    height: 30px;
    border-radius: 50%;
    background: var(--color-gold);
    border: none;
    color: var(--color-dark);
    font-size: 0.8rem;
    cursor: pointer;
    opacity: 0;
    transition: var(--transition-smooth);
}

.track-item:hover .track-play-btn {
    opacity: 1;
}

.track-info-wrapper {
    display: flex;
    flex-direction: column;
}

.track-title-name {
    color: var(--color-warm-white);
    font-weight: 600;
    margin-bottom: 0.3rem;
}

.track-album-name {
    color: rgba(245, 245, 220, 0.6);
    font-size: 0.9rem;
}

.track-duration {
    color: rgba(245, 245, 220, 0.5);
    font-size: 0.9rem;
}

.streaming-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.streaming-platforms {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    gap: 2rem;
    margin-top: 4rem;
}

.platform-link {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
    padding: 2rem;
    background: var(--color-dark);
    border: 2px solid rgba(212, 175, 55, 0.2);
    border-radius: 15px;
    color: var(--color-gold);
    text-decoration: none;
    transition: var(--transition-smooth);
    min-width: 150px;
}

.platform-link:hover {
    border-color: var(--color-gold);
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(212, 175, 55, 0.3);
}

.platform-link span {
    font-weight: 600;
}

.contact-section {
    padding: 6rem 0;
    background: var(--color-anthracite);
}

.contact-grid {
    display: grid;
    grid-template-columns: 1fr 1.2fr;
    gap: 4rem;
    margin-top: 3rem;
}

.contact-info {
    background: var(--color-dark);
    padding: 3rem;
    border-radius: 15px;
}

.contact-intro {
    font-size: 1.1rem;
    line-height: 1.8;
    margin-bottom: 2rem;
    color: rgba(245, 245, 220, 0.9);
}

.contact-methods {
    margin-bottom: 3rem;
}

.contact-method {
    display: flex;
    align-items: start;
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.method-icon {
    font-size: 2rem;
    min-width: 50px;
}

.method-info h3 {
    color: var(--color-gold);
    font-size: 1.2rem;
    margin-bottom: 0.5rem;
}

.method-info a {
    color: var(--color-warm-white);
    text-decoration: none;
    transition: var(--transition-smooth);
}

.method-info a:hover {
    color: var(--color-gold);
}

.contact-social h3 {
    color: var(--color-gold);
    margin-bottom: 1rem;
}

.contact-form-container {
    background: var(--color-dark);
    padding: 3rem;
    border-radius: 15px;
}

.contact-form {
    margin-top: 2rem;
}

.form-group {
    margin-bottom: 1.5rem;
}

.form-group label {
    display: block;
    color: var(--color-gold);
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 1rem;
    background: var(--color-anthracite);
    border: 1px solid rgba(212, 175, 55, 0.2);
    border-radius: 8px;
    color: var(--color-warm-white);
    font-family: 'Montserrat', sans-serif;
    font-size: 1rem;
    transition: var(--transition-smooth);
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--color-gold);
    box-shadow: 0 0 0 3px rgba(212, 175, 55, 0.1);
}

.submit-button {
    width: 100%;
    padding: 1.2rem 3rem;
    background: linear-gradient(135deg, var(--color-gold), var(--color-yellow));
    color: var(--color-dark);
    border: none;
    border-radius: 50px;
    font-weight: 700;
    font-size: 1.1rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.submit-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 30px rgba(212, 175, 55, 0.6);
}

.alert {
    padding: 1rem;
    border-radius: 8px;
    margin-bottom: 2rem;
}

.alert-success {
    background: rgba(76, 175, 80, 0.2);
    border: 1px solid rgba(76, 175, 80, 0.5);
    color: #4caf50;
}

.alert-error {
    background: rgba(244, 67, 54, 0.2);
    border: 1px solid rgba(244, 67, 54, 0.5);
    color: #f44336;
}

.footer-section {
    flex: 1;
    min-width: 250px;
}

.footer-links {
    list-style: none;
    padding: 0;
}

.footer-links li {
    margin-bottom: 0.8rem;
}

.footer-links a {
    color: var(--color-warm-white);
    text-decoration: none;
    transition: var(--transition-smooth);
}

.footer-links a:hover {
    color: var(--color-gold);
}

@media (max-width: 768px) {
    .container {
        padding: 0 1.5rem;
    }

    .mobile-menu-toggle {
        display: flex;
    }

    .mobile-menu-toggle.active span:nth-child(1) {
        transform: rotate(45deg) translate(5px, 5px);
    }

    .mobile-menu-toggle.active span:nth-child(2) {
        opacity: 0;
    }

    .mobile-menu-toggle.active span:nth-child(3) {
        transform: rotate(-45deg) translate(7px, -6px);
    }

    .nav-menu {
        position: fixed;
        top: 80px;
        left: -100%;
        width: 100%;
        height: calc(100vh - 80px);
        background: rgba(10, 10, 10, 0.98);
        flex-direction: column;
        padding: 2rem;
        transition: var(--transition-smooth);
        z-index: 999;
    }

    .nav-menu.active {
        left: 0;
    }

    .nav-link {
        font-size: 1.1rem;
        padding: 1rem 0;
    }

    .hero-title {
        font-size: 2rem;
    }

    .hero-subtitle {
        font-size: 0.85rem;
        letter-spacing: 0.15em;
    }

    .section-title {
        font-size: 1.8rem;
    }

    .page-title {
        font-size: 2rem;
    }

    .cta-button {
        padding: 0.7rem 1.5rem;
        font-size: 0.85rem;
    }

    .cta-button-secondary {
        padding: 0.6rem 1.2rem;
        font-size: 0.8rem;
    }

    .bio-text {
        font-size: 0.95rem;
    }

    .highlight-text {
        font-size: 1.1rem;
    }

    .bio-grid {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .about-bio-grid {
        grid-template-columns: 1fr;
    }

    .about-bio-image {
        position: relative;
        top: 0;
    }

    .qualities-grid {
        grid-template-columns: 1fr;
    }

    .blog-grid {
        grid-template-columns: 1fr;
    }

    .albums-grid {
        grid-template-columns: 1fr;
    }

    .contact-grid {
        grid-template-columns: 1fr;
    }

    .streaming-platforms {
        flex-direction: column;
        align-items: center;
    }

    .track-item {
        grid-template-columns: 40px 1fr auto;
        padding: 1rem;
    }

    .track-play {
        display: none;
    }

    .clips-grid {
        grid-template-columns: 1fr;
    }

    .clip-title {
        font-size: 1.1rem;
    }

    .event-card {
        flex-direction: column;
        text-align: center;
        padding: 1.5rem;
    }

    .event-venue {
        font-size: 1.1rem;
    }

    .event-time {
        font-size: 0.9rem;
    }

    .event-button {
        padding: 0.6rem 1.2rem;
        font-size: 0.8rem;
    }

    .date-day {
        font-size: 1.8rem;
    }

    .date-month {
        font-size: 0.8rem;
    }

    .gallery-grid {
        grid-template-columns: 1fr;
    }

    .footer-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .social-links {
        justify-content: center;
    }

    .footer h3 {
        font-size: 1.3rem;
    }

    .lightbox-prev,
    .lightbox-next {
        font-size: 2rem;
        padding: 0.5rem;
    }

    .lightbox-prev {
        left: 1rem;
    }

    .lightbox-next {
        right: 1rem;
    }

    .bio-section, .clips-section, .events-section, .gallery-section {
        padding: 4rem 0;
    }

    .submit-button {
        font-size: 1rem;
        padding: 1rem 2rem;
    }

    .testimonials-section {
        padding: 4rem 0;
    }

    .testimonials-slider {
        padding: 0 2.5rem;
    }

    .testimonial-card {
        padding: 2rem 1.5rem;
    }

    .testimonial-quote {
        font-size: 1rem;
    }

    .testimonial-author {
        font-size: 1rem;
    }

    .testimonial-role {
        font-size: 0.85rem;
    }

    .slider-nav {
        width: 40px;
        height: 40px;
        font-size: 1.2rem;
    }

    .testimonial-stars {
        font-size: 1rem;
    }
}

